import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, useMap } from 'react-leaflet';
import L from 'leaflet';

// --- STYLES (Self-Contained) ---
// To resolve the file path errors, all necessary styles are now embedded
// directly within this single file, making it self-contained and runnable.
const DashboardStyles = () => (
  <style>{`
    /* --- Main Layout --- */
    .dashboard-layout {
      display: flex;
      height: calc(100vh - 70px); /* Full height minus header */
      background-color: #f3f4f6;
    }

    .map-column {
      flex: 3; /* Map takes up 3 parts of the space */
      background-color: #e5e7eb; /* Fallback color */
    }

    .controls-column {
      flex: 2; /* Controls take up 2 parts of the space */
      background-color: #ffffff;
      padding: 1.5rem 2rem;
      box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05);
      display: flex;
      flex-direction: column;
      overflow-y: auto;
    }

    /* --- Generic Panel Styles --- */
    .panel-container {
      display: flex;
      flex-direction: column;
      height: 100%;
    }

    .panel-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 1.5rem;
      border-bottom: 1px solid #e5e7eb;
      padding-bottom: 1rem;
    }

    .panel-header h3 {
      margin: 0;
      font-size: 1.5rem;
      color: #111827;
    }

    .manage-link {
      text-decoration: none;
      color: #4f46e5;
      font-weight: 600;
    }

    /* --- Contacts Panel Styles --- */
    .contact-list {
      flex-grow: 1; /* Takes available space */
    }

    .contact-item {
      display: flex;
      align-items: center;
      padding: 0.75rem 0;
      gap: 1rem;
    }

    .contact-avatar {
      width: 40px;
      height: 40px;
      border-radius: 50%;
      background-color: #e0e7ff;
      color: #4338ca;
      display: grid;
      place-items: center;
      font-weight: bold;
    }

    .contact-details {
      display: flex;
      flex-direction: column;
    }
    .contact-details strong { font-size: 1rem; color: #1f2937; }
    .contact-details span { font-size: 0.875rem; color: #6b7280; }

    .start-trip-button {
      width: 100%;
      padding: 1rem;
      margin-top: 1rem;
      font-size: 1.1rem;
      font-weight: bold;
      color: white;
      background-color: #6366f1;
      border: none;
      border-radius: 8px;
      cursor: pointer;
      transition: background-color 0.3s;
    }
    .start-trip-button:hover { background-color: #4f46e5; }
    .error-text, .empty-text { color: #6b7280; }

    /* --- Trip Status Panel Styles --- */
    .status-title { text-align: center; font-size: 1.5rem; color: #111827; margin-bottom: 2rem; }
    .timer-display { text-align: center; margin: auto; }
    .timer-display p { margin: 0; color: #6b7280; font-size: 1rem; letter-spacing: 1px; }
    .timer-display span { font-size: 4rem; font-weight: bold; color: #111827; line-height: 1; }
    .trip-actions { display: flex; gap: 1rem; margin-top: 2rem; }
    .share-button, .end-trip-button { flex: 1; padding: 1rem; font-size: 1rem; font-weight: bold; border-radius: 8px; cursor: pointer; border: none; }
    .share-button { background-color: #f3f4f6; color: #1f2937; }
    .end-trip-button { background-color: #ef4444; color: white; }
  `}</style>
);

// --- MARKER ICON FIX ---
// This is a known issue with React-Leaflet. This code fixes the missing marker icons.
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// --- SUB-COMPONENTS (Now defined inside the main file) ---

// Helper component to recenter the map view
const RecenterAutomatically = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    map.setView([position.lat, position.lng]);
  }, [position, map]);
  return null;
};

const MapView = ({ isTripActive }) => {
  const [currentPosition, setCurrentPosition] = useState({ lat: 26.7606, lng: 83.3732 });

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setCurrentPosition({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      () => console.error("Error getting user location.")
    );
  }, [isTripActive]);

  return (
    <MapContainer center={[currentPosition.lat, currentPosition.lng]} zoom={15} style={{ height: '100%', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={[currentPosition.lat, currentPosition.lng]} />
      <RecenterAutomatically position={currentPosition} />
    </MapContainer>
  );
};

const ContactsPanel = ({ contacts, loading, error, onStartTrip }) => {
  return (
    <div className="panel-container">
      <div className="panel-header">
        <h3>Trusted Contacts</h3>
        <Link to="/contacts" className="manage-link">Manage</Link>
      </div>
      <div className="contact-list">
        {loading && <p>Loading contacts...</p>}
        {error && <p className="error-text">{error}</p>}
        {!loading && !error && contacts.length === 0 && (
          <p className="empty-text">No contacts added yet. Please add contacts to enable trip sharing.</p>
        )}
        {!loading && !error && contacts.map(contact => (
          <div key={contact._id} className="contact-item">
            <div className="contact-avatar">{contact.name.charAt(0)}</div>
            <div className="contact-details">
              <strong>{contact.name}</strong>
              <span>{contact.phone}</span>
            </div>
          </div>
        ))}
      </div>
      <button className="start-trip-button" onClick={onStartTrip}>
        Start SafeWalk Trip
      </button>
    </div>
  );
};

const TripStatusPanel = ({ tripDetails, onEndTrip }) => {
  const [elapsedTime, setElapsedTime] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setElapsedTime(Math.floor((new Date() - tripDetails.startTime) / 1000));
    }, 1000);
    return () => clearInterval(timer);
  }, [tripDetails]);

  const formatTime = (totalSeconds) => {
    const hours = String(Math.floor(totalSeconds / 3600)).padStart(2, '0');
    const minutes = String(Math.floor((totalSeconds % 3600) / 60)).padStart(2, '0');
    const seconds = String(totalSeconds % 60).padStart(2, '0');
    return `${hours}:${minutes}:${seconds}`;
  };

  const handleShare = () => {
    const shareableLink = `https://your-domain.com/track/${tripDetails.id}`;
    navigator.clipboard.writeText(shareableLink);
    alert(`Trip link copied to clipboard!\n${shareableLink}`);
  };

  return (
    <div className="panel-container">
      <h3 className="status-title">Trip in Progress</h3>
      <div className="timer-display">
        <p>ELAPSED TIME</p>
        <span>{formatTime(elapsedTime)}</span>
      </div>
      <div className="trip-actions">
        <button className="share-button" onClick={handleShare}>Share Link</button>
        <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
      </div>
    </div>
  );
};


// --- MAIN DASHBOARD COMPONENT ---
const Dashboard = () => {
  const [isTripActive, setIsTripActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [tripDetails, setTripDetails] = useState(null);

  // Fetch trusted contacts when the dashboard loads
  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) throw new Error('No authorization token found.');

        const res = await axios.get('http://localhost:5000/api/contacts', {
          headers: { 'x-auth-token': token },
        });
        setContacts(res.data);
      } catch (err) {
        setError('Could not load contacts.');
      } finally {
        setLoading(false);
      }
    };
    fetchContacts();
  }, []);

  const handleStartTrip = () => {
    const newTrip = {
      id: `trip_${new Date().getTime()}`,
      startTime: new Date(),
    };
    setTripDetails(newTrip);
    setIsTripActive(true);
    alert("Your SafeWalk trip has started.");
  };

  const handleEndTrip = () => {
    setIsTripActive(false);
    setTripDetails(null);
    alert("Your trip has safely ended.");
  };

  return (
    <>
      <DashboardStyles />
      <div className="dashboard-layout">
        {/* Left Column: The Map */}
        <div className="map-column">
          <MapView isTripActive={isTripActive} />
        </div>

        {/* Right Column: The Controls */}
        <div className="controls-column">
          {isTripActive ? (
            <TripStatusPanel tripDetails={tripDetails} onEndTrip={handleEndTrip} />
          ) : (
            <ContactsPanel
              contacts={contacts}
              loading={loading}
              error={error}
              onStartTrip={handleStartTrip}
            />
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
