import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- CONFIG ---
const API_URL = 'http://172.18.24.116:5000'; // Ensure this matches your IP

// --- UTILS: GEOMETRY ---
const getDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371e3; 
    const Ï†1 = lat1 * Math.PI/180;
    const Ï†2 = lat2 * Math.PI/180;
    const Î”Ï† = (lat2-lat1) * Math.PI/180;
    const Î”Î» = (lon2-lon1) * Math.PI/180;
    const a = Math.sin(Î”Ï†/2) * Math.sin(Î”Ï†/2) + Math.cos(Ï†1) * Math.cos(Ï†2) * Math.sin(Î”Î»/2) * Math.sin(Î”Î»/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
};

const getDistanceFromLine = (p, a, b) => {
    const R = 6371e3;
    const d13 = getDistance(a.lat, a.lng, p.lat, p.lng) / R;
    const Î¸13 = Math.atan2(p.lng-a.lng, p.lat-a.lat);
    const Î¸12 = Math.atan2(b.lon-a.lng, b.lat-a.lat);
    return Math.asin(Math.sin(d13) * Math.sin(Î¸13-Î¸12)) * R;
};

