import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05); display: flex; flex-direction: column; overflow-y: hidden; }
    .panel-container { display: flex; flex-direction: column; height: 100%; position: relative; }
    .panel-header { display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid #e5e7eb; padding-bottom: 1rem; margin-bottom: 1rem; }
    .panel-header h3 { margin: 0; font-size: 1.25rem; color: #111827; }
    .trip-planning-module { flex-shrink: 0; }
    .destination-form label { font-weight: 600; color: #374151; display: block; margin-bottom: 0.5rem; }
    .search-input-group { display: flex; gap: 0.5rem; margin-bottom: 1rem; }
    .search-input-group input { flex-grow: 1; padding: 0.75rem; border: 1px solid #d1d5db; border-radius: 8px; font-size: 1rem; }
    .search-input-group button { background: linear-gradient(90deg, #b8369a, #6a11cb); color: white; border: none; border-radius: 8px; padding: 0 1rem; cursor: pointer; }
    .results-list { list-style: none; padding: 0; margin: 0; border: 1px solid #e5e7eb; border-radius: 8px; max-height: 150px; overflow-y: auto; }
    .results-list li { padding: 0.75rem 1rem; border-bottom: 1px solid #e5e7eb; cursor: pointer; font-size: 0.9rem; }
    .results-list li:hover { background-color: #f3f4f6; }
    .start-trip-button { width: 100%; padding: 1rem; margin-top: 1rem; font-size: 1.1rem; font-weight: bold; color: white; background: linear-gradient(90deg, #b669a4, #8d5ec1); border: none; border-radius: 8px; cursor: pointer; }
    .start-trip-button:disabled { background: #c4b5fd; cursor: not-allowed; }
    .contacts-module { flex-grow: 1; margin-top: 2rem; overflow-y: auto; }
    .manage-link { text-decoration: none; color: #4f46e5; font-weight: 600; }
    .contact-item { display: flex; align-items: center; padding: 0.75rem 0; gap: 1rem; border-bottom: 1px solid #f3f4f6; }
    .contact-avatar { width: 40px; height: 40px; border-radius: 50%; background-color: #e0e7ff; color: #4338ca; display: grid; place-items: center; font-weight: bold; }
    .contact-details { display: flex; flex-direction: column; }
    .status-title { text-align: center; font-size: 1.5rem; }
    .timer-display { text-align: center; margin: 2rem 0; }
    .timer-display span { font-size: 4rem; font-weight: bold; }
    .trip-actions { display: flex; gap: 1rem; margin-top: 2rem; }
    .share-button { flex: 1; padding: 1rem; background: #f3f4f6; color: #111; border: none; border-radius: 8px; cursor: pointer; font-weight: bold; }
    .end-trip-button { flex: 1; padding: 1rem; background: #ef4444; color: white; border: none; border-radius: 8px; cursor: pointer; font-weight: bold; }
    /* Modal Styles */
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; width: 90%; max-width: 400px; padding: 2rem; border-radius: 12px; }
    .modal-header { display: flex; justify-content: space-between; margin-bottom: 1.5rem; }
    .share-item { display: flex; justify-content: space-between; align-items: center; padding: 0.8rem; border-bottom: 1px solid #eee; }
    .wa-btn { background: #25D366; color: white; border: none; padding: 8px 12px; border-radius: 6px; cursor: pointer; font-weight: bold; font-size: 0.9rem; text-decoration: none; }
  `}</style>
);

// --- MAP LOADER ---
const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const css = document.createElement('link'); css.rel = 'stylesheet'; css.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(css);
  const script = document.createElement('script'); script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; script.onload = () => cb(); document.head.appendChild(script);
};

// --- MAP COMPONENT ---
const MapView = ({ userPosition, destination }) => {
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const userMarker = useRef(null);
  const destMarker = useRef(null);
  const line = useRef(null);

  useEffect(() => {
    loadLeaflet(() => {
      if (mapRef.current && !mapObj.current && window.L) {
        const L = window.L;
        // Fix Icons
        delete L.Icon.Default.prototype._getIconUrl;
        L.Icon.Default.mergeOptions({
            iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
            iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
            shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
        });
        
        mapObj.current = L.map(mapRef.current).setView([userPosition.lat, userPosition.lng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
        userMarker.current = L.marker([userPosition.lat, userPosition.lng]).addTo(mapObj.current);
      }
    });
  }, []);

  useEffect(() => {
    if (mapObj.current && window.L) {
      const L = window.L;
      // Force user position to float
      const lat = parseFloat(userPosition.lat);
      const lng = parseFloat(userPosition.lng);
      
      if(userMarker.current) userMarker.current.setLatLng([lat, lng]);
      
      if (destination) {
        const dLat = parseFloat(destination.lat);
        const dLng = parseFloat(destination.lon);
        
        if (!destMarker.current) destMarker.current = L.marker([dLat, dLng]).addTo(mapObj.current);
        else destMarker.current.setLatLng([dLat, dLng]);

        const path = [[lat, lng], [dLat, dLng]];
        if (!line.current) line.current = L.polyline(path, { color: '#4f46e5' }).addTo(mapObj.current);
        else line.current.setLatLngs(path);
        
        mapObj.current.fitBounds(path, { padding: [50, 50] });
      } else {
        if (destMarker.current) { destMarker.current.remove(); destMarker.current = null; }
        if (line.current) { line.current.remove(); line.current = null; }
      }
    }
  }, [userPosition, destination]);

  return <div ref={mapRef} style={{ width: '100%', height: '100%' }} />;
};

// --- BEFORE TRIP PANEL ---
const BeforeTripPanel = ({ contacts, loading, searchTerm, setSearchTerm, onSearch, results, onSelectDest, dest, onStart, searchError }) => (
    <div className="panel-container">
        <div className="trip-planning-module">
          <h3>Start a New Trip</h3>
          <div className="destination-form">
            <label>Where are you going?</label>
            <div className="search-input-group">
              <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              <button onClick={onSearch}>Search</button>
            </div>
            {searchError && <p style={{color:'red'}}>{searchError}</p>}
            {results.length > 0 && (
              <ul className="results-list">
                {results.slice(0, 5).map((r) => (
                  <li key={r.place_id} onClick={() => onSelectDest({lat: r.lat, lon: r.lon, display_name: r.display_name})} style={{background: dest?.place_id === r.place_id ? '#e0e7ff' : ''}}>
                    {r.display_name}
                  </li>
                ))}
              </ul>
            )}
            <button className="start-trip-button" onClick={onStart} disabled={!dest}>Start SafeWalk Trip</button>
          </div>
        </div>
        <div className="contacts-module">
          <div className="panel-header"><h3>Trusted Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
          <div className="contact-list">
            {loading && <p>Loading...</p>}
            {contacts.map(c => (
              <div key={c._id} className="contact-item">
                <div className="contact-avatar">{c.name.charAt(0)}</div>
                <div className="contact-details"><strong>{c.name}</strong><span>{c.phone}</span></div>
              </div>
            ))}
          </div>
        </div>
    </div>
);

// --- TRIP STATUS PANEL ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts }) => {
    const [time, setTime] = useState(0);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const start = tripDetails?.startedAt ? new Date(tripDetails.startedAt) : new Date();
        const i = setInterval(() => setTime(Math.floor((new Date() - start) / 1000)), 1000);
        return () => clearInterval(i);
    }, [tripDetails]);

    const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

    const handleShare = (contact) => {
        const link = `${window.location.origin}/track/${tripDetails._id}`;
        const msg = `ðŸš¨ I started a SafeWalk trip! Track me here:\n${link}`;
        const phone = contact.phone.replace(/[^0-9]/g, '');
        window.open(`https://api.whatsapp.com/send?phone=${phone}&text=${encodeURIComponent(msg)}`, '_blank');
    };

    return (
        <div className="panel-container">
            <h3 className="status-title">Trip in Progress</h3>
            <div className="timer-display"><p>ELAPSED TIME</p><span>{format(time)}</span></div>
            <div className="trip-actions">
                <button className="share-button" onClick={() => setShowModal(true)}>Share Link</button>
                <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
            </div>
            {showModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <div className="modal-header"><h3>Share</h3><button onClick={()=>setShowModal(false)}>X</button></div>
                        {contacts.map(c => (
                            <div key={c._id} className="share-item">
                                <div><strong>{c.name}</strong><br/>{c.phone}</div>
                                <button className="wa-btn" onClick={() => handleShare(c)}>Share on WhatsApp</button>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

// --- DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [destination, setDestination] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const [searchError, setSearchError] = useState('');
  
  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // 1. Connect Socket
    socketRef.current = io('http://localhost:5000');

    // 2. Get Location
    navigator.geolocation.getCurrentPosition(
      (p) => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }),
      (e) => console.error(e)
    );

    // 3. Get Contacts
    const token = localStorage.getItem('token');
    if(!token) { navigate('/login'); return; }
    
    axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } })
         .then(res => setContacts(res.data))
         .catch(e => console.error(e))
         .finally(() => setLoading(false));

    return () => {
      if(socketRef.current) socketRef.current.disconnect();
      if(watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, [navigate]);

  const handleSearch = async () => {
    try {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      if(res.data.length === 0) setSearchError("No results.");
      setResults(res.data);
    } catch(e) { setSearchError("Search failed."); }
  };

  const startTrip = async () => {
    if(!destination) return alert("Select Destination");
    const token = localStorage.getItem('token');
    try {
        // Create Trip
        const res = await axios.post('http://localhost:5000/api/trips/start', {
            destination: { name: destination.display_name, lat: destination.lat, lon: destination.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);
        
        // Join Room
        socketRef.current.emit('joinTripRoom', res.data._id);
        
        // Watch
        watchIdRef.current = navigator.geolocation.watchPosition(p => {
             const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
             setUserPos(coords);
             socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        }, console.error, { enableHighAccuracy: true });

    } catch(e) { alert("Error starting trip"); }
  };

  // --- THIS IS THE CORRECTED END TRIP FUNCTION ---
  const handleEndTrip = async () => {
    // 1. Stop GPS Watch
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    
    const token = localStorage.getItem('token');
    
    // 2. Notify Server via Socket (So trusted contacts see "Ended" instantly)
    if(socketRef.current && tripDetails) {
        socketRef.current.emit('endTrip', tripDetails._id);
    }

    // 3. Notify Database (So future page loads show "Ended")
    if(tripDetails) {
        try {
            await axios.put(`http://localhost:5000/api/trips/${tripDetails._id}/end`, {}, { headers: { 'x-auth-token': token } });
        } catch(e) { console.error("DB End Error", e); }
    }

    // 4. Reset Local State
    setIsActive(false);
    setTripDetails(null);
    setDestination(null);
    setSearchTerm('');
    setResults([]);
    alert("Trip ended safely.");
  };

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column"><MapView userPosition={userPos} destination={destination} /></div>
      <div className="controls-column">
        {isActive ? (
          <TripStatusPanel tripDetails={tripDetails} onEndTrip={handleEndTrip} contacts={contacts} />
        ) : (
          <BeforeTripPanel
            contacts={contacts} loading={loading} searchTerm={searchTerm}
            setSearchTerm={setSearchTerm} onSearch={handleSearch} results={results}
            onSelectDest={setDestination} dest={destination} onStart={startTrip} searchError={searchError}
          />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
