import React, { useState } from "react";
impo
import { Link, useNavigate } from "react-router-dom";
import { saveAuthToken } from "../utils/auth"; // ✅ make sure this exists

// --- Styles (can stay inline or move to CSS file) ---
const LoginStyles = () => (
  <style>
    {`
      .auth-container {
        display: flex;
        justify-content: right;
        align-items: flex-start;
        padding: 2rem 1rem;
        min-height: auto;
        height: auto;
      }

      .auth-form-wrapper {
        background: #ffffff;
        padding: 0.5rem 2rem;
        border-radius: 18px;
        box-shadow: 0 8px 30px rgba(0, 0, 0, 0.08);
        width: 50%;
        max-width: 350px;
        text-align: center;
        margin-top: 60px;
        margin-right: 12%;
      }

      .auth-form-wrapper h2 {
        font-size: 2rem;
        color: #333;
        margin-bottom: 0.5rem;
        font-weight: 700;
      }

      .auth-subtext {
        color: #666;
        margin-bottom: 2rem;
      }

      .error-message {
        color: #e74c3c;
        background-color: #fbecec;
        border: 1px solid #e74c3c;
        padding: 0.75rem;
        border-radius: 8px;
        margin-bottom: 1rem;
        text-align: left;
      }

      .auth-form {
        display: flex;
        flex-direction: column;
        gap: 1.25rem;
        text-align: left;
      }

      .input-group {
        display: flex;
        flex-direction: column;
      }

      .input-group label {
        font-weight: 600;
        color: #444;
        margin-bottom: 0.5rem;
      }

      .input-group input {
        padding: 0.6rem 1rem;
        border: 1px solid #ddd;
        border-radius: 8px;
        font-size: 1rem;
      }

      .input-group input:focus {
        outline: none;
        border-color: #b8369a;
        box-shadow: 0 0 0 3px rgba(184, 54, 154, 0.15);
      }

      .auth-button {
        background: linear-gradient(90deg, #b8369a, #d54ca0);
        color: white;
        border: none;
        padding: 0.6rem;
        border-radius: 8px;
        cursor: pointer;
        font-weight: 600;
        font-size: 1rem;
      }

      .auth-switch {
        margin-top: 1.5rem;
        color: #555;
      }

      .auth-switch a {
        color: #b8369a;
        font-weight: 600;
        text-decoration: none;
      }
    `}
  </style>
);

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });

      // 1. Get the token from the successful response
      const { token } = response.data;

      // 2. Save the token to the browser's local storage
      localStorage.setItem('token', token);
      
      alert('Login successful! Redirecting to dashboard...');

      // --- THIS IS THE CRITICAL FIX ---
      // Instead of using navigate(), we use window.location.href.
      // This forces a full page reload, which guarantees that when the
      // PrivateRoute checks for the token, it will be there.
      window.location.href = '/dashboard';

    } catch (err) {
      setError(err.response?.data?.msg || 'Login failed. Please try again.');
    }
  };

  return (
    <>
      <LoginStyles />
      <div className="auth-container">
        {/* Your Login Form JSX */}
        <form onSubmit={handleLogin}>
          <h2>Welcome Back!</h2>
          {error && <p style={{color: 'red'}}>{error}</p>}
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
          <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
        </form>
      </div>
    </>
  );
};

export default Login;
