import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem; overflow-y: auto; }
    .panel-container { height: 100%; display: flex; flex-direction: column; }
    .destination-form label { font-weight: 600; color: #374151; display: block; margin-bottom: 0.5rem; }
    input { width: 100%; padding: 10px; margin-bottom: 10px; border: 1px solid #ddd; border-radius: 4px; box-sizing: border-box; }
    button { padding: 10px; border-radius: 4px; cursor: pointer; border: none; font-weight: bold; }
    .btn-search { background: #4f46e5; color: white; width: 100%; }
    .btn-start { background: #10b981; color: white; width: 100%; margin-top: 10px; }
    .btn-share { background: #25D366; color: white; flex: 1; margin-right: 10px; } /* WhatsApp Green */
    .btn-end { background: #ef4444; color: white; flex: 1; }
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; padding: 20px; width: 350px; border-radius: 8px; }
    .share-row { display: flex; justify-content: space-between; align-items: center; padding: 10px 0; border-bottom: 1px solid #eee; }
    .btn-wa { background: #25D366; color: white; border: none; padding: 5px 12px; border-radius: 4px; cursor: pointer; display: flex; align-items: center; gap: 5px; text-decoration: none; font-size: 0.9rem; }
    .results-list li { padding: 8px; cursor: pointer; border-bottom: 1px solid #eee; }
    .results-list li:hover { background: #f0f9ff; }
  `}</style>
);

// --- MAP LOADER ---
const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const link = document.createElement('link'); link.rel='stylesheet'; link.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(link);
  const script = document.createElement('script'); script.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; script.onload=cb; document.head.appendChild(script);
};

const MapView = ({ userPosition, destination }) => {
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const userMarker = useRef(null);
  const destMarker = useRef(null);
  const line = useRef(null);

  useEffect(() => {
    loadLeaflet(() => {
      if (window.L && mapRef.current && !mapObj.current) {
        const L = window.L;
        mapObj.current = L.map(mapRef.current).setView([26.76, 83.37], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
        userMarker.current = L.marker([26.76, 83.37]).addTo(mapObj.current);
      }
    });
  }, []);

  useEffect(() => {
     if(mapObj.current && window.L) {
         const L = window.L;
         userMarker.current.setLatLng([userPosition.lat, userPosition.lng]);
         if(destination) {
             if (!destMarker.current) destMarker.current = L.marker([destination.lat, destination.lon]).addTo(mapObj.current);
             else destMarker.current.setLatLng([destination.lat, destination.lon]);
             
             const path = [[userPosition.lat, userPosition.lng], [destination.lat, destination.lon]];
             if(!line.current) line.current = L.polyline(path, {color:'blue'}).addTo(mapObj.current);
             else line.current.setLatLngs(path);
             mapObj.current.fitBounds(path, {padding:[50,50]});
         } else {
            if(destMarker.current) { destMarker.current.remove(); destMarker.current = null; }
            if(line.current) { line.current.remove(); line.current = null; }
         }
     }
  }, [userPosition, destination]);

  return <div ref={mapRef} style={{width:'100%', height:'100%'}}></div>;
};

// --- TRIP STATUS PANEL (With WhatsApp Logic) ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts }) => {
  const [time, setTime] = useState(0);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const i = setInterval(() => setTime(Math.floor((new Date() - new Date(tripDetails.startedAt)) / 1000)), 1000);
    return () => clearInterval(i);
  }, [tripDetails]);

  const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

  // --- THIS IS THE WHATSAPP LOGIC ---
  const getWhatsAppLink = (phone) => {
    // 1. Clean the phone number (remove +, -, spaces)
    const cleanPhone = phone.replace(/[^0-9]/g, ''); 
    // 2. Build the tracking link
    const trackLink = `${window.location.origin}/track/${tripDetails._id}`;
    // 3. Create the message
    const message = `ðŸš¨ I started a SafeWalk trip! Track my live location here: ${trackLink}`;
    // 4. Return the full API URL
    return `https://wa.me/${cleanPhone}?text=${encodeURIComponent(message)}`;
  };

  return (
    <div className="panel-container">
      <h3 style={{textAlign:'center', color:'#10b981'}}>Trip In Progress</h3>
      <div className="timer-display" style={{textAlign:'center', margin:'2rem 0'}}>
        <p style={{margin:0, color:'#666'}}>ELAPSED TIME</p>
        <span style={{fontSize:'3.5rem', fontWeight:'bold'}}>{format(time)}</span>
      </div>
      
      <div style={{display:'flex', gap:'10px'}}>
        <button className="btn-share" onClick={() => setShowModal(true)}>Share via WhatsApp</button>
        <button className="btn-end" onClick={onEndTrip}>End Trip</button>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Share with... <button onClick={() => setShowModal(false)} style={{float:'right'}}>x</button></h3>
            {contacts.length === 0 ? <p>No contacts found.</p> : contacts.map(c => (
              <div key={c._id} className="share-row">
                <span>{c.name} ({c.phone})</span>
                {/* --- THIS LINK OPENS WHATSAPP --- */}
                <a 
                    href={getWhatsAppLink(c.phone)} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="btn-wa"
                >
                   Share 
                </a>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

// --- DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [destination, setDestination] = useState('');
  const [results, setResults] = useState([]);
  const [selectedDest, setSelectedDest] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const socketRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    socketRef.current = io('http://localhost:5000');
    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }));
    
    const token = localStorage.getItem('token');
    if(token) axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } }).then(res => setContacts(res.data)).catch(console.error);
    else navigate('/login');

    return () => socketRef.current.disconnect();
  }, [navigate]);

  const handleSearch = async () => {
    const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${destination}`);
    setResults(res.data);
  };

  const startTrip = async () => {
    if (!selectedDest) return alert("Select a destination!");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post('http://localhost:5000/api/trips/start', {
            destination: { name: selectedDest.display_name, lat: selectedDest.lat, lon: selectedDest.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);
        
        navigator.geolocation.watchPosition(p => {
             const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
             setUserPos(coords);
             socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        });
    } catch(e) { alert("Error starting trip"); }
  };

  const endTrip = () => {
    setIsActive(false);
    setTripDetails(null);
    setSelectedDest(null);
    setDestination('');
    setResults([]);
    alert("Trip Ended.");
    window.location.reload(); // Clean reset
  };

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column"><MapView userPosition={userPos} destination={selectedDest} /></div>
      <div className="controls-column">
        {isActive ? (
          <TripStatusPanel tripDetails={tripDetails} onEndTrip={endTrip} contacts={contacts} socket={socketRef.current} />
        ) : (
          <div className="panel-container">
            <h3>Start a New Trip</h3>
            <div className="search-input-group">
                <input value={destination} onChange={e => setDestination(e.target.value)} placeholder="Where to?" />
                <button className="btn-search" onClick={handleSearch}>Search</button>
            </div>
            <ul className="results-list">
                {results.slice(0,3).map(r => (
                    <li key={r.place_id} onClick={() => setSelectedDest({lat:r.lat, lon:r.lon, display_name:r.display_name})} style={{background: selectedDest?.place_id === r.place_id ? '#e0e7ff' : ''}}>
                        {r.display_name}
                    </li>
                ))}
            </ul>
            <button className="btn-start" onClick={startTrip} disabled={!selectedDest}>Start SafeWalk Trip</button>
            
            <div style={{marginTop:'20px'}}>
                <h4>Trusted Contacts <Link to="/contacts" style={{fontSize:'0.8rem'}}>Manage</Link></h4>
                {contacts.map(c => <div key={c._id} style={{padding:'5px'}}>ðŸ‘¤ {c.name}</div>)}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
