import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

// Assuming your styles are in a separate file or embedded
const LoginStyles = () => <style>{`/* Your login styles here */`}</style>;

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });

      // 1. Get the token from the successful response
      const { token } = response.data;

      // 2. Save the token to the browser's local storage
      localStorage.setItem('token', token);
      
      alert('Login successful! Redirecting to dashboard...');

      // --- THIS IS THE CRITICAL FIX ---
      // Instead of using navigate(), we use window.location.href.
      // This forces a full page reload, which guarantees that when the
      // PrivateRoute checks for the token, it will be there.
      window.location.href = '/dashboard';

    } catch (err) {
      setError(err.response?.data?.msg || 'Login failed. Please try again.');
    }
  };

  return (
    <>
      <LoginStyles />
      <div className="auth-container">
        {/* Your Login Form JSX */}
        <form onSubmit={handleLogin}>
          <h2>Welcome Back!</h2>
          {error && <p style={{color: 'red'}}>{error}</p>}
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
          <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
        </form>
      </div>
    </>
  );
};

export default Login;
