import React, { useState, useEffect } from 'react';
// Import useLocation to detect URL changes
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  // 1. We get the current URL location object from the router.
  const location = useLocation();

  // 2. THIS IS THE CRITICAL FIX:
  // This 'useEffect' hook will now re-run EVERY time the URL changes.
  useEffect(() => {
    // It checks if a login token exists in the browser's storage.
    const token = localStorage.getItem('token');
    // It updates the state, forcing the header to show the correct button.
    setIsLoggedIn(!!token); 
  }, [location]); // <-- This 'location' dependency is the key.

  return (
    <header className="header"> {/* Make sure you have CSS for this class */}
      <div className="logo"><Link to="/">SheSecure</Link></div>
      <div className="header-right">
        <nav className="nav-links">
          <Link to="/#features">Features</Link>
          <Link to="/#howitworks">How It Works</Link>
          <Link to="/#about">About</Link>
        </nav>
        <div className="get-started">
          {isLoggedIn ? (
            // If logged in, it shows a link that goes to your Logout page
            <Link to="/logout" className="btn-primary">Logout</Link>
          ) : (
            // If not logged in, it shows this Get Started button
            <Link to="/login" className="btn-primary">Get Started</Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
