import React, { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import "./style/Header.css";

// --- AUTH HELPERS ---
const getAuthToken = () => localStorage.getItem('token');
const clearAuthToken = () => { localStorage.removeItem('token'); };

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = getAuthToken();
    setIsLoggedIn(!!token);
  }, [location]);

  const handleLogout = () => {
    clearAuthToken();
    setIsLoggedIn(false);
    navigate("/");
  };

  return (
    <header className="header">
      <Link to="/" className="logo">SheSecure</Link>
      
      <div className="header-right">
        <nav className="nav-links">
          {isLoggedIn ? (
            <>
              <Link 
                to="/dashboard" 
                className={location.pathname === '/dashboard' ? 'nav-item-active' : ''}
              >
                Dashboard
              </Link>
              <Link 
                to="/community-map" 
                className={location.pathname === '/community-map' ? 'nav-item-active' : ''}
              >
                Community Map
              </Link>
              <Link 
                to="/contacts" 
                className={location.pathname === '/contacts' ? 'nav-item-active' : ''}
              >
                Contacts
              </Link>
            </>
          ) : (
            <>
              <a href="/#features">Features</a>
              <a href="/#howitworks">How It Works</a>
            </>
          )}
        </nav>
        
        <div className="get-started">
          {isLoggedIn ? (
            <button className="btn-logout" onClick={handleLogout}>Logout</button>
          ) : (
            <Link className="btn-primary" to="/signup">Get Started</Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
