import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { saveAuthToken } from "../utils/auth"; // âœ… make sure this exists

// --- Styles (can stay inline or move to CSS file) ---
const LoginStyles = () => (
  <style>
    {`
      .auth-container {
        display: flex;
        justify-content: right;
        align-items: flex-start;
        padding: 2rem 1rem;
        min-height: auto;
        height: auto;
      }

      .auth-form-wrapper {
        background: #ffffff;
        padding: 0.5rem 2rem;
        border-radius: 18px;
        box-shadow: 0 8px 30px rgba(0, 0, 0, 0.08);
        width: 50%;
        max-width: 350px;
        text-align: center;
        margin-top: 60px;
        margin-right: 12%;
      }

      .auth-form-wrapper h2 {
        font-size: 2rem;
        color: #333;
        margin-bottom: 0.5rem;
        font-weight: 700;
      }

      .auth-subtext {
        color: #666;
        margin-bottom: 2rem;
      }

      .error-message {
        color: #e74c3c;
        background-color: #fbecec;
        border: 1px solid #e74c3c;
        padding: 0.75rem;
        border-radius: 8px;
        margin-bottom: 1rem;
        text-align: left;
      }

      .auth-form {
        display: flex;
        flex-direction: column;
        gap: 1.25rem;
        text-align: left;
      }

      .input-group {
        display: flex;
        flex-direction: column;
      }

      .input-group label {
        font-weight: 600;
        color: #444;
        margin-bottom: 0.5rem;
      }

      .input-group input {
        padding: 0.6rem 1rem;
        border: 1px solid #ddd;
        border-radius: 8px;
        font-size: 1rem;
      }

      .input-group input:focus {
        outline: none;
        border-color: #b8369a;
        box-shadow: 0 0 0 3px rgba(184, 54, 154, 0.15);
      }

      .auth-button {
        background: linear-gradient(90deg, #b8369a, #d54ca0);
        color: white;
        border: none;
        padding: 0.6rem;
        border-radius: 8px;
        cursor: pointer;
        font-weight: 600;
        font-size: 1rem;
      }

      .auth-switch {
        margin-top: 1.5rem;
        color: #555;
      }

      .auth-switch a {
        color: #b8369a;
        font-weight: 600;
        text-decoration: none;
      }
    `}
  </style>
);

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  // --- ACTION: Get the current location object from the router ---
  const location = useLocation();

  // --- THIS IS THE CRITICAL FIX ---
  useEffect(() => {
    // This function checks if a token exists in local storage
    const checkAuthStatus = () => {
      setIsLoggedIn(!!localStorage.getItem('token'));
    };
    
    checkAuthStatus(); // Run the check
    
  }, [location]); // <-- The magic is here. This tells React to re-run this effect EVERY time the URL changes.

  return (
    <header className="header">
      <div className="logo"><Link to="/">SheSecure</Link></div>
      <div className="header-right">
        <nav className="nav-links">
          <Link to="/">Features</Link>
          <Link to="/">How It Works</Link>
        </nav>
        {isLoggedIn ? (
          <Link to="/logout" className="btn-primary">Logout</Link>
        ) : (
          <Link to="/login" className="btn-primary">Get Started</Link>
        )}
      </div>
    </header>
  );
};

const Footer = () => <footer className="footer">Â© 2025 SafeWalk. All rights reserved.</footer>;

const PrivateRoute = ({ children }) => {
  const isAuthenticated = !!localStorage.getItem('token');
  if (!isAuthenticated) {
    // If the user is not authenticated, redirect them to the login page
    return <Navigate to="/login" replace />;
  }
  return children;
};


// --- PAGE COMPONENTS (Placeholders for demonstration) ---

const LandingPage = () => <div className="placeholder-page"><h1>Landing Page Content</h1></div>;
const Signup = () => <div className="placeholder-page"><h1>Signup Page</h1></div>;
const Login = () => <div className="placeholder-page"><h1>Login Page</h1></div>;
const Dashboard = () => <div className="placeholder-page"><h1>Dashboard</h1><p>Welcome! This page is protected.</p></div>;
const ManageContacts = () => <div className="placeholder-page"><h1>Manage Contacts</h1><p>This page is also protected.</p></div>;

const Logout = () => {
  const navigate = useNavigate();
  useEffect(() => {
    localStorage.removeItem('token');
    // We don't need the dispatchEvent anymore because the header now listens to location changes
    navigate('/');
  }, [navigate]);
  return <div className="placeholder-page"><h1>Logging you out...</h1></div>;
};


// --- MAIN APP ROUTER ---

function App() {
  return (
    <Router>
      <AppStyles />
      <div className="App">
        <Header />
        <main>
          <Routes>
            {/* --- Public Routes --- */}
            <Route path="/" element={<LandingPage />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/logout" element={<Logout />} />
            
            {/* --- Protected Routes --- */}
            <Route 
              path="/dashboard" 
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              } 
            />
            <Route 
              path="/contacts" 
              element={
                <PrivateRoute>
                  <ManageContacts />
                </PrivateRoute>
              } 
            />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
