// client/src/components/Navbar.js
import React, { useState } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './style/Navbar.css';

const Navbar = () => {
    const [click, setClick] = useState(false);
    const { isLoggedIn, logout } = useAuth();
    const navigate = useNavigate();

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const handleLogout = () => {
        logout();
        closeMobileMenu();
        navigate('/');
    };

    return (
        <>
            <nav className="navbar">
                <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
                    SheSecure
                </Link>
                <div className="hamburger" onClick={handleClick}>
                    <span className={click ? "bar" : "bar"} style={{ transform: click ? "rotate(45deg) translate(5px, 6px)" : "" }}></span>
                    <span className={click ? "bar" : "bar"} style={{ opacity: click ? 0 : 1 }}></span>
                    <span className={click ? "bar" : "bar"} style={{ transform: click ? "rotate(-45deg) translate(5px, -6px)" : "" }}></span>
                </div>

                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    {isLoggedIn ? (
                        <>
                            <li className='nav-item'>
                                <NavLink to='/dashboard' className={({ isActive }) => "nav-link" + (isActive ? " active" : "")} onClick={closeMobileMenu}>
                                    Dashboard
                                </NavLink>
                            </li>
                            <li className='nav-item'>
                                <NavLink to='/community-map' className={({ isActive }) => "nav-link" + (isActive ? " active" : "")} onClick={closeMobileMenu}>
                                    Community Map
                                </NavLink>
                            </li>
                            <li className='nav-item'>
                                <NavLink to='/contacts' className={({ isActive }) => "nav-link" + (isActive ? " active" : "")} onClick={closeMobileMenu}>
                                    Contacts
                                </NavLink>
                            </li>
                            <li className='nav-item mobile-logout'>
                                <button className='nav-btn btn-logout' onClick={handleLogout}>Logout</button>
                            </li>
                        </>
                    ) : (
                        <>
                            <li className='nav-item'>
                                <a href='/#Challenges' className='nav-link' onClick={closeMobileMenu}>Challenges</a>
                            </li>
                            <li className='nav-item'>
                                <a href='/#Solution' className='nav-link' onClick={closeMobileMenu}>Solution</a>
                            </li>
                            <li className='nav-item'>
                                <a href='/#howitworks' className='nav-link' onClick={closeMobileMenu}>How It Works</a>
                            </li>
                            <li className='nav-item'>
                                <Link to='/signup' className='nav-btn btn-primary' onClick={closeMobileMenu}>Get Started</Link>
                            </li>
                        </>
                    )}
                </ul>
            </nav>
            {/* Overlay to close menu when clicking outside on mobile */}
            {click && <div style={{ position: 'fixed', top: '70px', left: 0, width: '100%', height: '100%', background: 'rgba(0,0,0,0.5)', zIndex: 9998 }} onClick={closeMobileMenu}></div>}
        </>
    );
};

export default Navbar;
