import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, Navigate, useLocation } from 'react-router-dom';
import { io } from 'socket.io-client';

// --- IMPORT YOUR PAGES ---
// Ensure these files exist in src/Pages/
import Dashboard from './Pages/Dashboard';
import ManageContacts from './Pages/ManageContacts';
import TrackTripPage from './Pages/TrackTripPage';
import Signup from './Pages/Signup';
import Login from './Pages/Login';

// --- STYLES ---
const AppStyles = () => (
  <style>{`
    body { font-family: sans-serif; margin: 0; background-color: #f9fafb; }
    main { padding-top: 70px; min-height: calc(100vh - 140px); }
    .header { display: flex; justify-content: space-between; align-items: center; padding: 0 2rem; height: 70px; background: white; position: fixed; top: 0; width: 100%; z-index: 1000; box-shadow: 0 2px 5px rgba(0,0,0,0.1); box-sizing: border-box; }
    .header .logo a { font-size: 1.5rem; font-weight: bold; text-decoration: none; color: #333; }
    .nav-links { display: flex; gap: 1rem; align-items: center; }
    .btn-primary { background: #6366f1; color: white; padding: 8px 16px; border-radius: 6px; text-decoration: none; border: none; cursor: pointer; font-weight: bold; }
    .footer { padding: 2rem; background: #1f2937; color: white; text-align: center; }
    .placeholder-page { padding: 4rem; text-align: center; }
    
    /* ALERT POPUP STYLES */
    .alert-popup { position: fixed; top: 80px; right: 20px; background: white; border-left: 5px solid #ef4444; padding: 20px; border-radius: 8px; box-shadow: 0 10px 30px rgba(0,0,0,0.3); z-index: 9999; width: 300px; animation: slideIn 0.5s; }
    .alert-btn { background: #ef4444; color: white; border: none; width: 100%; padding: 10px; margin-top: 10px; border-radius: 4px; cursor: pointer; font-weight: bold; }
    @keyframes slideIn { from { transform: translateX(120%); } to { transform: translateX(0); } }
    
    /* SOCKET STATUS INDICATOR */
    .socket-status { font-size: 0.8rem; margin-right: 15px; display: flex; align-items: center; gap: 5px; }
  `}</style>
);

// --- GLOBAL NOTIFICATION LISTENER (Embedded) ---
const GlobalListener = () => {
  const [alert, setAlert] = useState(null);
  const [status, setStatus] = useState('disconnected');
  const navigate = useNavigate();

  useEffect(() => {
    const socket = io('http://localhost:5000');

    socket.on('connect', () => setStatus('connected'));
    socket.on('disconnect', () => setStatus('disconnected'));

    socket.on('receiveNotification', (data) => {
      console.log("ðŸš¨ ALERT RECEIVED:", data);
      // Play sound
      try { new Audio('https://actions.google.com/sounds/v1/alarms/beep_short.ogg').play().catch(()=>{}); } catch(e){}
      setAlert(data);
    });

    return () => socket.disconnect();
  }, []);

  return (
    <>
      {/* Connection Indicator in Header */}
      <div style={{position: 'fixed', top: '80px', left: '20px', zIndex: 9000, background: 'rgba(0,0,0,0.7)', color: 'white', padding: '5px 10px', borderRadius: '20px', fontSize: '12px'}}>
        {status === 'connected' ? 'ðŸŸ¢ System Active' : 'ðŸ”´ System Disconnected'}
      </div>

      {alert && (
        <div className="alert-popup">
          <h3 style={{color: '#ef4444', margin: '0 0 10px 0'}}>ðŸš¨ SAFETY ALERT</h3>
          <p>{alert.message}</p>
          <button className="alert-btn" onClick={() => { navigate(`/track/${alert.tripId}`); setAlert(null); }}>
            TRACK LOCATION
          </button>
          <button onClick={() => setAlert(null)} style={{background:'none', border:'none', width:'100%', marginTop:'5px', color:'#666', cursor:'pointer'}}>Dismiss</button>
        </div>
      )}
    </>
  );
};

// --- AUTH COMPONENTS ---
const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();
  useEffect(() => setIsLoggedIn(!!localStorage.getItem('token')), [location]);

  return (
    <header className="header">
      <div className="logo"><Link to="/">SheSecure</Link></div>
      <div className="nav-links">
        {isLoggedIn ? <Link to="/logout" className="btn-primary">Logout</Link> : <Link to="/login" className="btn-primary">Login</Link>}
      </div>
    </header>
  );
};

const PrivateRoute = ({ children }) => localStorage.getItem('token') ? children : <Navigate to="/login" replace />;
const Logout = () => {
    const navigate = useNavigate();
    useEffect(() => { localStorage.removeItem('token'); navigate('/'); }, [navigate]);
    return <div className="placeholder-page">Logging out...</div>;
};

// Page Placeholders (if not imported)
const LandingPage = () => <div className="placeholder-page"><h1>Welcome to SheSecure</h1><p>Your safety companion.</p><Link to="/signup" className="btn-primary">Sign Up Now</Link></div>;

function App() {
  return (
    <Router>
      <AppStyles />
      <div className="App">
        <Header />
        <GlobalListener /> {/* This listens for alerts on ALL pages */}
        <main>
          <Routes>
            <Route path="/" element={<LandingPage />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/logout" element={<Logout />} />
            <Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />
            <Route path="/contacts" element={<PrivateRoute><ManageContacts /></PrivateRoute>} />
            <Route path="/track/:tripId" element={<TrackTripPage />} />
          </Routes>
        </main>
        <div className="footer">Â© 2025 SheSecure</div>
      </div>
    </Router>
  );
}

export default App;
