import React, { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import "./style/Header.css"; // MAKE SURE THIS PATH IS CORRECT

// --- AUTH HELPERS ---
const getAuthToken = () => localStorage.getItem('token');
const clearAuthToken = () => { localStorage.removeItem('token'); };

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    // Check login status on mount and route change
    const token = getAuthToken();
    setIsLoggedIn(!!token);
    // Close menu automatically when route changes
    setMenuOpen(false);
  }, [location]);

  const handleLogout = () => {
    clearAuthToken();
    setIsLoggedIn(false);
    navigate("/");
    setMenuOpen(false);
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className="header">
      {/* 1. Logo */}
      <Link to="/" className="logo" onClick={() => setMenuOpen(false)}>SheSecure</Link>

      <div className="header-right">
        {/* 2. Navigation Menu */}
        {/* We use specific classes to force visibility */}
        <nav className={`nav-links ${menuOpen ? "nav-active" : ""}`}>
          {isLoggedIn ? (
            <>
              <Link to="/dashboard" className="nav-item">Dashboard</Link>
              <Link to="/community-map" className="nav-item">Community Map</Link>
              <Link to="/contacts" className="nav-item">Contacts</Link>
            </>
          ) : (
            <>
              <Link to="/#Challenges" className="nav-item">Challenges</Link>
              <Link to="/#Solution" className="nav-item">Solution</Link>
              <Link to="/#howitworks" className="nav-item">How It Works</Link>
            </>
          )}
        </nav>

        {/* 3. Action Group (Hamburger + Button) */}
        <div className="action-group">
          
          {/* Hamburger Icon */}
          <div className="hamburger" onClick={toggleMenu}>
            <span className={menuOpen ? "bar open" : "bar"}></span>
            <span className={menuOpen ? "bar open" : "bar"}></span>
            <span className={menuOpen ? "bar open" : "bar"}></span>
          </div>

          {/* Main Button */}
          <div className="auth-button-container">
            {isLoggedIn ? (
              <button className="btn-logout" onClick={handleLogout}>Logout</button>
            ) : (
              <Link className="btn-primary" to="/signup">Get Started</Link>
            )}
          </div>
        </div>
      </div>
      
      {/* Overlay: Closes menu when clicking the dark background */}
      {menuOpen && <div className="menu-overlay" onClick={() => setMenuOpen(false)}></div>}
    </header>
  );
};

export default Header;
