import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, Navigate } from 'react-router-dom';

// --- STYLES (Self-Contained) ---
// To resolve the file path errors, all necessary styles and components are now
// defined within this single file, making it self-contained and runnable.
const AppStyles = () => (
  <style>
    {`
      body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; margin: 0; background-color: #f9fafb; }
      .App { text-align: center; }
      main { padding-top: 70px; /* Space for the fixed header */ min-height: calc(100vh - 130px); }
      .placeholder-page { padding: 4rem 2rem; }
      
      /* Header Styles */
      .header {
        display: flex; justify-content: space-between; align-items: center;
        padding: 0 2rem; height: 70px; background-color: white; color: #111827;
        position: fixed; top: 0; left: 0; width: 100%; z-index: 1000;
        box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1);
        box-sizing: border-box;
      }
      .header .logo a { font-size: 1.5rem; font-weight: bold; text-decoration: none; color: inherit; }
      .header-right { display: flex; align-items: center; gap: 2rem; }
      .nav-links { display: flex; gap: 1.5rem; }
      .nav-links a { color: #4b5563; text-decoration: none; font-weight: 500; }
      .btn-primary {
        background-color: #6366f1; color: white; padding: 10px 20px;
        border: none; border-radius: 8px; font-weight: bold; cursor: pointer;
        transition: background-color 0.3s;
      }
      .btn-primary:hover { background-color: #4f46e5; }

      /* Footer Styles */
      .footer { padding: 2rem; background-color: #111827; color: white; }
    `}
  </style>
);


// --- CORE APP COMPONENTS (Self-Contained) ---

const Header = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Function to check authentication status
    const checkAuthStatus = () => {
      const token = localStorage.getItem('token');
      setIsLoggedIn(!!token); // Convert token presence to a boolean
    };
    
    checkAuthStatus(); // Check when component mounts
    
    // Listen for storage changes (e.g., login/logout in another tab)
    window.addEventListener('storage', checkAuthStatus);
    
    // Cleanup listener on component unmount
    return () => {
      window.removeEventListener('storage', checkAuthStatus);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    alert('You have been logged out.');
    navigate('/');
  };

  return (
    <header className="header">
      <div className="logo"><Link to="/">SheSecure</Link></div>
      <div className="header-right">
        <nav className="nav-links">
          <Link to="/#features">Features</Link>
          <Link to="/#howitworks">How It Works</Link>
          <Link to="/#about">About</Link>
        </nav>
        {isLoggedIn ? (
          <button className="btn-primary" onClick={handleLogout}>Logout</button>
        ) : (
          <button className="btn-primary" onClick={() => navigate('/signup')}>Get Started</button>
        )}
      </div>
    </header>
  );
};

const Footer = () => <footer className="footer">© 2025 SafeWalk. All rights reserved.</footer>;

const PrivateRoute = ({ children }) => {
  const isAuthenticated = !!localStorage.getItem('token');
  if (!isAuthenticated) {
    // Redirect them to the /login page
    return <Navigate to="/login" replace />;
  }
  return children;
};


// --- PAGE COMPONENTS (Placeholders for demonstration) ---

const LandingPage = () => (
  <>
    <div className="placeholder-page"><h1>Herosection</h1></div>
    <div className="placeholder-page" style={{backgroundColor: '#fff'}}><h2>Challenges</h2></div>
    <div className="placeholder-page"><h2>Solution</h2></div>
    <div className="placeholder-page" style={{backgroundColor: '#fff'}}><h2>How It Works</h2></div>
    <div className="placeholder-page"><h2>Call To Action (CTA)</h2></div>
  </>
);

const Signup = () => <div className="placeholder-page"><h1>Signup Page</h1><p>Your signup form component would be rendered here.</p></div>;
const Login = () => <div className="placeholder-page"><h1>Login Page</h1><p>Your login form component would be rendered here.</p></div>;
const Dashboard = () => <div className="placeholder-page"><h1>Dashboard</h1><p>Welcome! This is a protected page.</p></div>;


// --- MAIN APP ROUTER ---

function App() {
  return (
    <Router>
      <AppStyles />
      <div className="App">
        <Header />
        <main>
          <Routes>
            {/* --- Public Routes --- */}
            <Route path="/" element={<LandingPage />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />

            {/* --- Protected Dashboard Route --- */}
            <Route 
              path="/dashboard"
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              } 
            />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
