import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const styles = {
  popup: {
    position: 'fixed', top: '20px', right: '20px', width: '300px',
    backgroundColor: '#fff', borderLeft: '6px solid #ef4444',
    boxShadow: '0 10px 25px rgba(0,0,0,0.2)', padding: '15px',
    borderRadius: '8px', zIndex: 9999, fontFamily: 'sans-serif'
  },
  header: { color: '#ef4444', fontWeight: 'bold', marginBottom: '10px' },
  btn: {
    width: '100%', padding: '8px', marginTop: '10px',
    backgroundColor: '#ef4444', color: 'white', border: 'none',
    borderRadius: '4px', cursor: 'pointer', fontWeight: 'bold'
  }
};

const NotificationListener = () => {
  const [alertData, setAlertData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Connect to socket
    const socket = io('http://localhost:5000');

    socket.on('receiveNotification', (data) => {
      console.log("üö® ALERT RECEIVED:", data);
      
      // Try to play sound
      try {
        new Audio('https://actions.google.com/sounds/v1/alarms/beep_short.ogg').play().catch(()=>{});
      } catch(e){}

      setAlertData(data);
    });

    return () => socket.disconnect();
  }, []);

  if (!alertData) return null;

  return (
    <div style={styles.popup}>
      <div style={styles.header}>‚ö†Ô∏è SAFETY ALERT</div>
      <div>{alertData.message}</div>
      <button style={styles.btn} onClick={() => {
          navigate(`/track/${alertData.tripId}`);
          setAlertData(null);
      }}>
        TRACK LIVE LOCATION
      </button>
      <div style={{textAlign:'center', marginTop:'5px', fontSize:'0.8rem', cursor:'pointer'}} onClick={() => setAlertData(null)}>Dismiss</div>
    </div>
  );
};

export default NotificationListener;
