import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

const API_URL = 'http://172.18.24.204:5000'; // Your IP

const TrackTripPage = () => {
  const { tripId } = useParams();
  const [trip, setTrip] = useState(null);
  const [livePos, setLivePos] = useState(null);
  const [status, setStatus] = useState("loading"); // loading, active, ended, sos
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const marker = useRef(null);

  // Map Loader
  const loadLeaflet = (cb) => {
    if (window.L) return cb();
    const s = document.createElement('script'); s.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload=cb; document.head.appendChild(s);
    const c = document.createElement('link'); c.rel='stylesheet'; c.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(c);
  };

  useEffect(() => {
    // 1. Fetch Trip Data
    axios.get(`${API_URL}/api/trips/${tripId}`).then(res => {
        setTrip(res.data);
        if (res.data.status === 'completed') setStatus('ended');
        else if (res.data.status === 'sos') setStatus('sos');
        else setStatus('active');

        if(res.data.path?.length > 0) setLivePos(res.data.path[res.data.path.length-1]);
    });

    // 2. Socket Listeners
    const socket = io(API_URL);
    socket.emit('joinTripRoom', tripId);
    
    socket.on('tripUpdate', (coords) => setLivePos(coords));
    
    // Listen for END
    socket.on('tripEnded', () => {
        setStatus('ended');
        alert("Trip has ended.");
    });
    
    // Listen for SOS (The Red Alert)
    socket.on('sosAlert', (data) => {
        console.log("SOS RECEIVED!");
        setStatus('sos');
        try { new Audio('https://actions.google.com/sounds/v1/alarms/beep_short.ogg').play().catch(()=>{}); } catch(e){}
    });

    // 3. Init Map
    loadLeaflet(() => {
        if(mapRef.current && !mapObj.current) {
            const L = window.L;
            mapObj.current = L.map(mapRef.current).setView([26.76, 83.37], 13);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
            marker.current = L.marker([26.76, 83.37]).addTo(mapObj.current);
        }
    });

    return () => socket.disconnect();
  }, [tripId]);

  // Update Marker
  useEffect(() => {
      if(livePos && mapObj.current && window.L) {
          const lat = parseFloat(livePos.lat);
          const lng = parseFloat(livePos.lng);
          if(!isNaN(lat)) {
             marker.current.setLatLng([lat, lng]);
             mapObj.current.setView([lat, lng]);
          }
      }
  }, [livePos]);

  // --- RENDER ---
  if (status === 'sos') {
      return (
          <div style={{height:'100vh', background:'red', color:'white', display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', textAlign:'center', animation:'flash 1s infinite'}}>
              <style>{`@keyframes flash { 0% {background:red;} 50% {background:darkred;} 100% {background:red;} }`}</style>
              <h1 style={{fontSize:'4rem'}}>⚠️ SOS ALERT ⚠️</h1>
              <h2>{trip?.userId?.fullName || 'USER'} NEEDS HELP!</h2>
              <div ref={mapRef} style={{width:'90%', height:'50%', background:'white', borderRadius:'10px', marginTop:'20px'}}></div>
          </div>
      );
  }

  return (
    <div style={{height: '100vh', display:'flex', flexDirection:'column'}}>
       <div style={{padding:'20px', background:'white', boxShadow:'0 2px 5px rgba(0,0,0,0.1)', zIndex:1000, textAlign:'center'}}>
           <h2>Tracking Trip</h2>
           {status === 'active' && <h3 style={{color:'green'}}>LIVE TRACKING</h3>}
           {status === 'ended' && <h3 style={{color:'red'}}>TRIP ENDED</h3>}
       </div>
       <div ref={mapRef} style={{flex:1}}></div>
    </div>
  );
};
export default TrackTripPage;
