import React, { useState, useEffect } from 'react';
// --- ACTION: Import useLocation ---
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, Navigate, useLocation } from 'react-router-dom';

// --- STYLES (Self-Contained) ---
const AppStyles = () => (
  <style>{`
    body { 
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; 
      margin: 0; 
      background-color: #f9fafb; 
    }
    .App { text-align: center; }
    main { 
      padding-top: 70px; /* Space for the fixed header */ 
      min-height: calc(100vh - 130px); /* Full height minus header and footer */
    }
    .placeholder-page { 
      padding: 4rem 2rem; 
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
    }
    
    /* Header Styles */
    .header {
      display: flex; justify-content: space-between; align-items: center;
      padding: 0 2rem; height: 70px; background-color: white; color: #111827;
      position: fixed; top: 0; left: 0; width: 100%; z-index: 1000;
      box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1);
      box-sizing: border-box;
    }
    .header .logo a { font-size: 1.5rem; font-weight: bold; text-decoration: none; color: inherit; }
    .header-right { display: flex; align-items: center; gap: 2rem; }
    .nav-links { display: flex; gap: 1.5rem; }
    .nav-links a { color: #4b5563; text-decoration: none; font-weight: 500; }
    .btn-primary {
      background-color: #6366f1; color: white; padding: 10px 20px;
      border: none; border-radius: 8px; font-weight: bold; cursor: pointer;
      text-decoration: none; display: inline-block;
    }
    .btn-primary:hover { background-color: #4f46e5; }

    /* Footer Styles */
    .footer { padding: 2rem; background-color: #111827; color: white; }
  `}</style>
);


// --- CORE APP COMPONENTS (Self-Contained for Demonstration) ---

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  // --- ACTION: Get the current location object from the router ---
  const location = useLocation();

  // --- THIS IS THE CRITICAL FIX ---
  useEffect(() => {
    // This function checks if a token exists in local storage
    const checkAuthStatus = () => {
      setIsLoggedIn(!!localStorage.getItem('token'));
    };
    
    checkAuthStatus(); // Run the check
    
  }, [location]); // <-- The magic is here. This tells React to re-run this effect EVERY time the URL changes.

  return (
    <header className="header">
      <div className="logo"><Link to="/">SheSecure</Link></div>
      <div className="header-right">
        <nav className="nav-links">
          <Link to="/">Features</Link>
          <Link to="/">How It Works</Link>
        </nav>
        {isLoggedIn ? (
          <Link to="/logout" className="btn-primary">Logout</Link>
        ) : (
          <Link to="/login" className="btn-primary">Get Started</Link>
        )}
      </div>
    </header>
  );
};

const Footer = () => <footer className="footer">Â© 2025 SafeWalk. All rights reserved.</footer>;

const PrivateRoute = ({ children }) => {
  const isAuthenticated = !!localStorage.getItem('token');
  if (!isAuthenticated) {
    // If the user is not authenticated, redirect them to the login page
    return <Navigate to="/login" replace />;
  }
  return children;
};


// --- PAGE COMPONENTS (Placeholders for demonstration) ---

const LandingPage = () => <div className="placeholder-page"><h1>Landing Page Content</h1></div>;
const Signup = () => <div className="placeholder-page"><h1>Signup Page</h1></div>;
const Login = () => <div className="placeholder-page"><h1>Login Page</h1></div>;
const Dashboard = () => <div className="placeholder-page"><h1>Dashboard</h1><p>Welcome! This page is protected.</p></div>;
const ManageContacts = () => <div className="placeholder-page"><h1>Manage Contacts</h1><p>This page is also protected.</p></div>;

const Logout = () => {
  const navigate = useNavigate();
  useEffect(() => {
    localStorage.removeItem('token');
    // We don't need the dispatchEvent anymore because the header now listens to location changes
    navigate('/');
  }, [navigate]);
  return <div className="placeholder-page"><h1>Logging you out...</h1></div>;
};


// --- MAIN APP ROUTER ---

function App() {
  return (
    <Router>
      <AppStyles />
      <div className="App">
        <Header />
        <main>
          <Routes>
            {/* --- Public Routes --- */}
            <Route path="/" element={<LandingPage />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/logout" element={<Logout />} />
            
            {/* --- Protected Routes --- */}
            <Route 
              path="/dashboard" 
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              } 
            />
            <Route 
              path="/contacts" 
              element={
                <PrivateRoute>
                  <ManageContacts />
                </PrivateRoute>
              } 
            />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
