// --- MAP LOADING HELPER ---
const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const css = document.createElement('link'); css.rel = 'stylesheet'; css.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(css);
  const script = document.createElement('script'); script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; script.onload = () => cb(); document.head.appendChild(script);
};

// --- MAP VIEW COMPONENT ---
const MapView = ({ userPosition, destination }) => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const userMarkerRef = useRef(null);
  const destMarkerRef = useRef(null);
  const polylineRef = useRef(null);

  useEffect(() => {
    loadLeaflet(() => {
      if (!mapContainerRef.current || !window.L) return;
      const L = window.L;

      // FIX: Move icon configuration INSIDE the callback where L is defined
      delete L.Icon.Default.prototype._getIconUrl;
      L.Icon.Default.mergeOptions({
        iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
        iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
        shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
      });

      if (!mapInstanceRef.current) {
        mapInstanceRef.current = L.map(mapContainerRef.current).setView([userPosition.lat, userPosition.lng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstanceRef.current);
        userMarkerRef.current = L.marker([userPosition.lat, userPosition.lng]).addTo(mapInstanceRef.current);
      }
    });
  }, []);

  useEffect(() => {
    if (mapInstanceRef.current && window.L) {
      const L = window.L;
      // Ensure coordinates are numbers
      const userLat = parseFloat(userPosition.lat);
      const userLng = parseFloat(userPosition.lng);

      if (userMarkerRef.current) userMarkerRef.current.setLatLng([userLat, userLng]);
      
      if (destination && destination.lat && destination.lon) {
        const destLat = parseFloat(destination.lat);
        const destLng = parseFloat(destination.lon);

        if (!destMarkerRef.current) destMarkerRef.current = L.marker([destLat, destLng]).addTo(mapInstanceRef.current);
        else destMarkerRef.current.setLatLng([destLat, destLng]);

        const path = [[userLat, userLng], [destLat, destLng]];
        if (!polylineRef.current) polylineRef.current = L.polyline(path, { color: '#4f46e5' }).addTo(mapInstanceRef.current);
        else polylineRef.current.setLatLngs(path);
        
        mapInstanceRef.current.fitBounds(path, { padding: [50, 50] });
      } else {
        if (destMarkerRef.current) { destMarkerRef.current.remove(); destMarkerRef.current = null; }
        if (polylineRef.current) { polylineRef.current.remove(); polylineRef.current = null; }
      }
    }
  }, [userPosition, destination]);

  return <div ref={mapContainerRef} style={{ width: '100%', height: '100%' }} />;
};

// --- TRIP STATUS PANEL ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts }) => {
  const [time, setTime] = useState(0);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (!tripDetails) return;
    const start = new Date(tripDetails.startedAt);
    const i = setInterval(() => setTime(Math.floor((new Date() - start) / 1000)), 1000);
    return () => clearInterval(i);
  }, [tripDetails]);

  const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

  const handleWhatsAppShare = (contact) => {
    if (!tripDetails?._id) return alert("Trip ID missing");
    
    const trackingLink = `${window.location.origin}/track/${tripDetails._id}`;
    // FIX: Message formatted with newlines for clarity
    const message = `ðŸš¨ I started a SafeWalk trip! \n\nTrack my live location here:\n${trackingLink}`;
    
    // Remove non-numeric characters for the API
    const phone = contact.phone.replace(/[^0-9]/g, ''); 
    
    // Use the universal API link
    const url = `https://api.whatsapp.com/send?phone=${phone}&text=${encodeURIComponent(message)}`;
    window.open(url, '_blank');
  };

  return (
    <div className="panel-container">
      <h3 style={{ textAlign: 'center', fontSize: '1.5rem', color: '#10b981' }}>Trip in Progress</h3>
      <div className="timer-display"><p style={{margin:0, color:'#666'}}>ELAPSED TIME</p><span>{format(time)}</span></div>
      
      <div className="trip-actions">
        <button className="share-button" onClick={() => setShowModal(true)}>Share Link</button>
        <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div style={{display:'flex', justifyContent:'space-between', marginBottom:'15px'}}>
               <h3>Share with Contacts</h3>
               <button onClick={() => setShowModal(false)} style={{border:'none', background:'none', fontSize:'1.5rem', cursor:'pointer'}}>Ã—</button>
            </div>
            {contacts.length === 0 ? <p>No contacts found. Add them in Manage Contacts.</p> : contacts.map(c => (
              <div key={c._id} className="share-item">
                <div><strong>{c.name}</strong><br/><small>{c.phone}</small></div>
                <button className="wa-btn" onClick={() => handleWhatsAppShare(c)}>Share on WhatsApp</button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

// --- MAIN DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [destination, setDestination] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const [searchError, setSearchError] = useState('');
  
  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    socketRef.current = io('http://localhost:5000');
    
    navigator.geolocation.getCurrentPosition(
      (p) => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }),
      (e) => console.error("Loc Error:", e)
    );

    const token = localStorage.getItem('token');
    if (token) {
        axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } })
             .then(res => setContacts(res.data))
             .catch(e => console.error("Contact Error:", e));
    } else {
        navigate('/login');
    }

    return () => {
      if (socketRef.current) socketRef.current.disconnect();
      if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, [navigate]);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    try {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      if (res.data.length === 0) setSearchError("No results found.");
      else {
          setResults(res.data);
          setSearchError("");
      }
    } catch (e) { setSearchError("Search failed."); }
  };

  const startTrip = async () => {
    if (!destination) return alert("Select a destination!");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post('http://localhost:5000/api/trips/start', 
            { destination: { name: destination.display_name, lat: destination.lat, lon: destination.lon } },
            { headers: { 'x-auth-token': token } }
        );
        
        setTripDetails(res.data);
        setIsActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);
        
        watchIdRef.current = navigator.geolocation.watchPosition(p => {
             const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
             setUserPos(coords);
             socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        }, console.error, { enableHighAccuracy: true });

    } catch (e) { alert("Error starting trip. Check console."); console.error(e); }
  };

  const endTrip = async () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    const token = localStorage.getItem('token');
    
    // Notify DB
    if(tripDetails) {
        try {
            await axios.put(`http://localhost:5000/api/trips/${tripDetails._id}/end`, {}, { headers: { 'x-auth-token': token } });
        } catch(e) { console.error("End trip error", e); }
    }
    
    // Notify Socket
    if(socketRef.current && tripDetails) {
        socketRef.current.emit('endTrip', tripDetails._id);
    }

    setIsActive(false);
    setTripDetails(null);
    setDestination(null);
    setSearchTerm('');
    setResults([]);
    alert("Trip ended safely.");
  };

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column">
        <MapView userPosition={userPos} destination={destination} />
      </div>
      <div className="controls-column">
        {isActive ? (
          <TripStatusPanel tripDetails={tripDetails} onEndTrip={endTrip} contacts={contacts} />
        ) : (
          <div className="panel-container">
            <div className="trip-planning-module">
              <h3>Start a New Trip</h3>
              <div className="destination-form">
                <label>Where are you going?</label>
                <div className="search-input-group">
                  <input value={searchTerm} onChange={e => setSearchTerm(e.target.value)} placeholder="Search..." onKeyPress={e => e.key === 'Enter' && handleSearch()} />
                  <button onClick={handleSearch}>Search</button>
                </div>
                {searchError && <p className="search-error">{searchError}</p>}
                {results.length > 0 && (
                  <ul className="results-list">
                    {results.slice(0, 5).map(r => (
                      <li key={r.place_id} onClick={() => setDestination({lat:r.lat, lon:r.lon, display_name:r.display_name})} style={{background: destination?.place_id === r.place_id ? '#e0e7ff' : ''}}>
                        {r.display_name}
                      </li>
                    ))}
                  </ul>
                )}
                <button className="start-trip-button" onClick={startTrip} disabled={!destination}>Start SafeWalk Trip</button>
              </div>
            </div>
            <div className="contacts-module">
              <div className="panel-header"><h3>Trusted Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
              {contacts.map(c => (
                <div key={c._id} className="contact-item">
                  <div className="contact-avatar">{c.name.charAt(0)}</div>
                  <div className="contact-details"><strong>{c.name}</strong><span>{c.phone}</span></div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
