import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ManageContacts = () => {
  const [contacts, setContacts] = useState([]);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  useEffect(() => {
    if(!token) { navigate('/login'); return; }
    axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } })
         .then(res => setContacts(res.data))
         .catch(e => console.error(e));
  }, [token, navigate]);

  const addContact = async (e) => {
    e.preventDefault();
    try {
        const res = await axios.post('http://localhost:5000/api/contacts', { name, phone }, { headers: { 'x-auth-token': token } });
        setContacts(res.data);
        setName(''); setPhone('');
    } catch(e) { alert("Error adding contact"); }
  };

  return (
    <div style={{padding: '4rem'}}>
      <h2>Manage Contacts</h2>
      <form onSubmit={addContact}>
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} style={{padding: '10px', marginRight: '10px'}} required/>
        <input placeholder="Phone" value={phone} onChange={e => setPhone(e.target.value)} style={{padding: '10px', marginRight: '10px'}} required/>
        <button type="submit" style={{padding: '10px'}}>Add</button>
      </form>
      <ul>
        {contacts.map(c => <li key={c._id} style={{padding: '10px'}}>{c.name} - {c.phone}</li>)}
      </ul>
    </div>
  );
};

export default ManageContacts;
