import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

// --- CONFIG ---
const API_URL = 'http://172.18.24.204:5000'; // Your IP

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; display: flex; flex-direction: column; overflow-y: hidden; }
    .panel-container { display: flex; flex-direction: column; height: 100%; }
    .trip-planning-module { flex-shrink: 0; }
    .contacts-module { flex-grow: 1; margin-top: 2rem; overflow-y: auto; }
    .panel-header { display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid #e5e7eb; padding-bottom: 1rem; margin-bottom: 1rem; }
    input { width: 100%; padding: 10px; margin-bottom: 10px; border: 1px solid #ddd; border-radius: 4px; box-sizing: border-box; }
    button { padding: 10px; border-radius: 4px; cursor: pointer; border: none; font-weight: bold; }
    .btn-search { background: #4f46e5; color: white; width: 100%; }
    .btn-start { background: #10b981; color: white; width: 100%; margin-top: 10px; }
    .btn-share { background: #3b82f6; color: white; flex: 1; margin-right: 10px; }
    .btn-end { background: #ef4444; color: white; flex: 1; }
    .results-list li { padding: 8px; cursor: pointer; border-bottom: 1px solid #eee; }
    .results-list li:hover { background: #f0f9ff; }
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; padding: 20px; width: 350px; border-radius: 8px; }
    .share-row { display: flex; justify-content: space-between; align-items: center; padding: 10px 0; border-bottom: 1px solid #eee; }
    .btn-wa { background: #25D366; color: white; border: none; padding: 5px 10px; border-radius: 4px; font-weight: bold; }
  `}</style>
);

// --- LEAFLET LOADER ---
const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const c = document.createElement('link'); c.rel='stylesheet'; c.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(c);
  const s = document.createElement('script'); s.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload=cb; document.head.appendChild(s);
};

// --- MAP COMPONENT ---
const MapView = ({ userPosition, destination }) => {
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const userMarker = useRef(null);
  const destMarker = useRef(null);
  const line = useRef(null);

  useEffect(() => {
    loadLeaflet(() => {
      const L = window.L;
      if (mapRef.current && !mapObj.current) {
        // Initialize Map
        mapObj.current = L.map(mapRef.current).setView([26.76, 83.37], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
        
        // Custom Icon to prevent crash
        const icon = new L.Icon({
           iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
           shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
           iconSize: [25, 41], iconAnchor: [12, 41]
        });
        userMarker.current = L.marker([26.76, 83.37], {icon}).addTo(mapObj.current);
      }
    });
  }, []);

  useEffect(() => {
    if (mapObj.current && window.L) {
      const L = window.L;
      // SAFE UPDATE: user location
      const lat = parseFloat(userPosition.lat);
      const lng = parseFloat(userPosition.lng);
      if(!isNaN(lat) && !isNaN(lng) && userMarker.current) {
          userMarker.current.setLatLng([lat, lng]);
          // Only pan if we don't have a destination bounds set, or strictly follow user
          // mapObj.current.panTo([lat, lng]); 
      }

      // SAFE UPDATE: destination
      if (destination && destination.lat) {
        const dLat = parseFloat(destination.lat);
        const dLng = parseFloat(destination.lon);
        
        if (!destMarker.current) {
             const icon = new L.Icon({
                iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-red.png',
                shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
                iconSize: [25, 41], iconAnchor: [12, 41]
             });
             destMarker.current = L.marker([dLat, dLng], {icon}).addTo(mapObj.current);
        } else {
             destMarker.current.setLatLng([dLat, dLng]);
        }

        const path = [[lat, lng], [dLat, dLng]];
        if (!line.current) line.current = L.polyline(path, { color: 'blue' }).addTo(mapObj.current);
        else line.current.setLatLngs(path);
        
        mapObj.current.fitBounds(path, { padding: [50, 50] });
      }
    }
  }, [userPosition, destination]);

  return <div ref={mapRef} style={{ width: '100%', height: '100%' }} />;
};

// --- MAIN DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [destination, setDestination] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const [time, setTime] = useState(0);
  const [showModal, setShowModal] = useState(false);
  
  const navigate = useNavigate();
  // We use a Ref to store the interval ID so we can stop it later
  const uploadInterval = useRef(null);

  useEffect(() => {
    // 1. Get Initial Location
    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }));

    // 2. Fetch Contacts
    const token = localStorage.getItem('token');
    if(!token) { navigate('/login'); return; }
    axios.get(`${API_URL}/api/contacts`, { headers: { 'x-auth-token': token } })
         .then(res => setContacts(res.data))
         .catch(e => console.error("Contact load error"));

    // Cleanup on unmount
    return () => {
        if(uploadInterval.current) clearInterval(uploadInterval.current);
    };
  }, [navigate]);

  // Search Logic
  const handleSearch = async () => {
    try {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      setResults(res.data);
    } catch(e) { alert("Search failed"); }
  };

  // START TRIP (POLLING SENDER)
  const startTrip = async () => {
    if(!destination) return alert("Select destination");
    const token = localStorage.getItem('token');
    
    try {
        // 1. Create Trip in DB
        const res = await axios.post(`${API_URL}/api/trips/start`, {
            destination: { name: destination.display_name, lat: destination.lat, lon: destination.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);

        // 2. Start Polling Loop (Send location every 4 seconds)
        // This replaces WebSockets
        uploadInterval.current = setInterval(() => {
            navigator.geolocation.getCurrentPosition(p => {
                const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
                setUserPos(coords); // Update local map
                
                // Upload to DB
                axios.put(`${API_URL}/api/trips/${res.data._id}/location`, coords, {
                    headers: { 'x-auth-token': token }
                }).catch(err => console.log("Loc upload fail (minor)"));
            });
        }, 4000); 

    } catch(e) { alert("Start failed"); }
  };

  // END TRIP
  const endTrip = async () => {
    // 1. Stop uploading
    if(uploadInterval.current) clearInterval(uploadInterval.current);
    
    // 2. Tell DB trip is over
    const token = localStorage.getItem('token');
    await axios.put(`${API_URL}/api/trips/${tripDetails._id}/end`, {}, { headers: { 'x-auth-token': token } });

    // 3. Reset UI
    setIsActive(false);
    setTripDetails(null);
    setDestination(null);
    setSearchTerm('');
    setResults([]);
    alert("Trip ended safely.");
  };

  // Timer Logic
  useEffect(() => {
      let t;
      if(isActive) t = setInterval(() => setTime(prev => prev + 1), 1000);
      else setTime(0);
      return () => clearInterval(t);
  }, [isActive]);

  const formatTime = (s) => new Date(s * 1000).toISOString().substr(11, 8);

  const handleShare = (contact) => {
     const link = `${window.location.origin}/track/${tripDetails._id}`;
     const text = `ðŸš¨ I started a SafeWalk trip! Track me here:\n${link}`;
     const phone = contact.phone.replace(/[^0-9]/g, '');
     window.open(`https://api.whatsapp.com/send?phone=${phone}&text=${encodeURIComponent(text)}`, '_blank');
  };

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column"><MapView userPosition={userPos} destination={destination} /></div>
      <div className="controls-column">
        {isActive ? (
          <div className="panel-container">
            <h3 style={{textAlign:'center', color:'#10b981'}}>Trip In Progress</h3>
            <div className="timer-display"><p>ELAPSED TIME</p><span>{formatTime(time)}</span></div>
            <div className="trip-actions">
                <button className="btn-share" onClick={() => setShowModal(true)}>Share Link</button>
                <button className="btn-end" onClick={endTrip}>End Trip</button>
            </div>
            {showModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Share <button onClick={()=>setShowModal(false)} style={{float:'right', border:'none'}}>x</button></h3>
                        {contacts.map(c => (
                            <div key={c._id} className="share-row">
                                <strong>{c.name}</strong>
                                <button className="btn-wa" onClick={() => handleShare(c)}>WhatsApp</button>
                            </div>
                        ))}
                    </div>
                </div>
            )}
          </div>
        ) : (
          <div className="panel-container">
             <h3>Start a New Trip</h3>
             <div style={{display:'flex', gap:'10px', marginBottom:'10px'}}>
                 <input value={searchTerm} onChange={e=>setSearchTerm(e.target.value)} placeholder="Destination" />
                 <button className="btn-search" onClick={handleSearch}>Search</button>
             </div>
             <ul className="results-list">
                 {results.slice(0,3).map(r => (
                     <li key={r.place_id} onClick={() => setDestination(r)} style={{background: destination?.place_id === r.place_id ? '#e0e7ff':''}}>
                        {r.display_name}
                     </li>
                 ))}
             </ul>
             <button className="btn-start" disabled={!destination} onClick={startTrip}>Start Trip</button>
             <div className="contacts-module">
                 <div className="panel-header"><h3>Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
                 {contacts.map(c => <div key={c._id} style={{padding:'10px', borderBottom:'1px solid #eee'}}>ðŸ‘¤ {c.name}</div>)}
             </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
