import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05); display: flex; flex-direction: column; overflow-y: hidden; }
    .panel-container { display: flex; flex-direction: column; height: 100%; }
    /* ... (Your existing styles remain here) ... */
    .start-trip-button { width: 100%; padding: 1rem; margin-top: 1rem; font-size: 1.1rem; font-weight: bold; color: white; background: linear-gradient(90deg, #b669a4, #8d5ec1); border: none; border-radius: 8px; cursor: pointer; }
    .end-trip-button { flex: 1; padding: 1rem; background: #ef4444; color: white; border: none; border-radius: 8px; cursor: pointer; font-weight: bold; }
    .share-button { flex: 1; padding: 1rem; background: #f3f4f6; color: #111; border: none; border-radius: 8px; cursor: pointer; font-weight: bold; margin-right: 10px; }
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; width: 90%; max-width: 400px; padding: 2rem; border-radius: 12px; }
    .share-item { display: flex; justify-content: space-between; align-items: center; padding: 0.8rem; border-bottom: 1px solid #eee; }
    .wa-btn { background: #25D366; color: white; border: none; padding: 8px 12px; border-radius: 6px; cursor: pointer; font-weight: bold; font-size: 0.9rem; text-decoration: none; }
    /* ... rest of styles ... */
  `}</style>
);

// --- LOADERS & MAPVIEW (Simplified for brevity, keep your existing logic) ---
const loadLeafletCSS = () => { if (!document.getElementById('leaflet-css')) { const l = document.createElement('link'); l.id='leaflet-css'; l.rel='stylesheet'; l.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(l); } };
const loadLeafletScript = (cb) => { if (window.L) return cb(); const s = document.createElement('script'); s.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload=cb; document.head.appendChild(s); };

const MapView = ({ userPosition, destination }) => {
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const marker = useRef(null);
  
  useEffect(() => {
    loadLeafletCSS();
    loadLeafletScript(() => {
      const L = window.L;
      if (mapRef.current && !mapObj.current) {
        mapObj.current = L.map(mapRef.current).setView([userPosition.lat, userPosition.lng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
        marker.current = L.marker([userPosition.lat, userPosition.lng]).addTo(mapObj.current);
      }
    });
  }, []);

  useEffect(() => {
      if(mapObj.current && window.L) {
          marker.current.setLatLng([userPosition.lat, userPosition.lng]);
          mapObj.current.panTo([userPosition.lat, userPosition.lng]);
      }
  }, [userPosition]);

  return <div ref={mapRef} style={{ width: '100%', height: '100%' }} />;
};

// --- TRIP STATUS PANEL ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts }) => {
    const [time, setTime] = useState(0);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const start = tripDetails.startedAt ? new Date(tripDetails.startedAt) : new Date();
        const i = setInterval(() => setTime(Math.floor((new Date() - start) / 1000)), 1000);
        return () => clearInterval(i);
    }, [tripDetails]);

    const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

    const handleWhatsAppShare = (contact) => {
        // Use your IP Address for the link so phones can open it
        const trackingLink = `http://172.18.24.204:3000/track/${tripDetails._id}`;
        const message = `ðŸš¨ I started a SafeWalk trip! Track my live location here:\n${trackingLink}`;
        const phone = contact.phone.replace(/[^0-9]/g, ''); 
        const url = `https://api.whatsapp.com/send?phone=${phone}&text=${encodeURIComponent(message)}`;
        window.open(url, '_blank');
    };

    return (
        <div className="panel-container">
            <h3 style={{textAlign:'center', fontSize:'1.5rem'}}>Trip in Progress</h3>
            <div style={{textAlign:'center', margin:'2rem 0'}}><p>ELAPSED TIME</p><span style={{fontSize:'4rem', fontWeight:'bold'}}>{format(time)}</span></div>
            <div style={{display:'flex', gap:'1rem'}}>
                <button className="share-button" onClick={() => setShowModal(true)}>Share Link</button>
                <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
            </div>
            {showModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Share with... <button onClick={()=>setShowModal(false)} style={{float:'right', border:'none', background:'none'}}>x</button></h3>
                        {contacts.map(c => (
                            <div key={c._id} className="share-item">
                                <div><strong>{c.name}</strong><br/>{c.phone}</div>
                                <button className="wa-btn" onClick={() => handleWhatsAppShare(c)}>Share on WhatsApp</button>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

// --- DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  
  // States for search
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [dest, setDest] = useState(null);
  
  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();

  // 1. Init
  useEffect(() => {
    socketRef.current = io('http://172.18.24.204:5000'); // Use your IP

    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }));
    
    const token = localStorage.getItem('token');
    if(!token) { navigate('/login'); return; }
    
    axios.get('http://172.18.24.204:5000/api/contacts', { headers: { 'x-auth-token': token } })
         .then(res => setContacts(res.data))
         .catch(console.error);

    return () => {
        if (socketRef.current) socketRef.current.disconnect();
        if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    }
  }, [navigate]);

  // 2. Start Trip
  const startTrip = async () => {
    if(!dest) return alert("Select Destination");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post('http://172.18.24.204:5000/api/trips/start', {
             destination: { name: dest.display_name, lat: dest.lat, lon: dest.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);
        
        watchIdRef.current = navigator.geolocation.watchPosition(p => {
             const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
             setUserPos(coords);
             socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        });
    } catch(e) { alert("Error starting trip"); }
  };

  // 3. Handle End Trip (FIXED)
  const handleEndTrip = async () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    const token = localStorage.getItem('token');
    
    // 1. Notify Socket (Instant UI update for tracker)
    socketRef.current.emit('endTrip', tripDetails._id);
    
    // 2. Notify Database (Permanent record)
    try {
        await axios.post(`http://172.18.24.204:5000/api/trips/${tripDetails._id}/end`, {}, { 
            headers: { 'x-auth-token': token } 
        });
    } catch(e) { console.error("DB End Error:", e); }

    // 3. Reset UI
    setIsActive(false);
    setTripDetails(null);
    setDest(null);
    setSearchTerm('');
    setResults([]);
    alert("Trip ended safely.");
  };

  // 4. Search Handler
  const handleSearch = async () => {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      setResults(res.data);
  }

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column"><MapView userPosition={userPos} destination={dest} /></div>
      <div className="controls-column">
        {isActive ? (
          <TripStatusPanel tripDetails={tripDetails} onEndTrip={handleEndTrip} contacts={contacts} />
        ) : (
          <div className="panel-container">
             <h3>Start a New Trip</h3>
             <div style={{display:'flex', gap:'10px', marginBottom:'10px'}}>
                 <input style={{flex:1, padding:'8px'}} value={searchTerm} onChange={e=>setSearchTerm(e.target.value)} placeholder="Destination..." />
                 <button onClick={handleSearch} style={{padding:'8px', background:'#4f46e5', color:'white', border:'none'}}>Search</button>
             </div>
             <ul>
                 {results.slice(0,3).map(r => (
                     <li key={r.place_id} onClick={() => setDest({lat:r.lat, lon:r.lon, display_name:r.display_name})} style={{padding:'8px', cursor:'pointer', background: dest?.place_id === r.place_id ? '#e0e7ff':''}}>
                        {r.display_name}
                     </li>
                 ))}
             </ul>
             <button className="start-trip-button" onClick={startTrip} disabled={!dest}>Start SafeWalk Trip</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
