import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05); display: flex; flex-direction: column; overflow-y: hidden; }
    .panel-container { display: flex; flex-direction: column; height: 100%; position: relative; }
    .panel-header { display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid #e5e7eb; padding-bottom: 1rem; margin-bottom: 1rem; }
    .destination-form label { font-weight: 600; color: #374151; display: block; margin-bottom: 0.5rem; }
    .search-input-group { display: flex; gap: 0.5rem; margin-bottom: 1rem; }
    .search-input-group input { flex-grow: 1; padding: 0.75rem; border: 1px solid #d1d5db; border-radius: 8px; font-size: 1rem; }
    .search-input-group button { background: #4f46e5; color: white; border: none; border-radius: 8px; padding: 0 1rem; cursor: pointer; }
    .results-list { list-style: none; padding: 0; margin: 0; border: 1px solid #e5e7eb; border-radius: 8px; max-height: 150px; overflow-y: auto; }
    .results-list li { padding: 0.75rem 1rem; border-bottom: 1px solid #e5e7eb; cursor: pointer; }
    .results-list li:hover { background-color: #f3f4f6; }
    .start-trip-button { width: 100%; padding: 1rem; margin-top: 1rem; font-size: 1.1rem; font-weight: bold; color: white; background: #6366f1; border: none; border-radius: 8px; cursor: pointer; }
    .start-trip-button:disabled { background: #c4b5fd; cursor: not-allowed; }
    .timer-display { text-align: center; margin: 2rem 0; }
    .timer-display span { font-size: 4rem; font-weight: bold; display: block; }
    .trip-actions { display: flex; gap: 1rem; }
    .share-button { flex: 1; padding: 1rem; background: #f3f4f6; border: none; border-radius: 8px; font-weight: bold; cursor: pointer; }
    .end-trip-button { flex: 1; padding: 1rem; background: #ef4444; color: white; border: none; border-radius: 8px; font-weight: bold; cursor: pointer; }
    /* Modal Styles */
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; width: 90%; max-width: 400px; padding: 2rem; border-radius: 12px; }
    .share-item { display: flex; justify-content: space-between; align-items: center; padding: 1rem; border-bottom: 1px solid #eee; }
    .send-btn { background: #10b981; color: white; border: none; padding: 0.5rem 1rem; border-radius: 6px; cursor: pointer; }
    .send-btn:disabled { background: #6ee7b7; cursor: default; }
  `}</style>
);

// --- HELPER: Load Leaflet from CDN (Fixes Build Errors) ---
const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const link = document.createElement('link'); link.rel='stylesheet'; link.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(link);
  const script = document.createElement('script'); script.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; script.onload=cb; document.head.appendChild(script);
};

// --- COMPONENT: MapView ---
const MapView = ({ userPosition, destination }) => {
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const userMarker = useRef(null);
  const destMarker = useRef(null);
  const line = useRef(null);

  useEffect(() => {
    loadLeaflet(() => {
      if (!mapObj.current && mapRef.current) {
        const L = window.L;
        mapObj.current = L.map(mapRef.current).setView([userPosition.lat, userPosition.lng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
        userMarker.current = L.marker([userPosition.lat, userPosition.lng]).addTo(mapObj.current);
      }
    });
  }, []); // Init once

  useEffect(() => {
    if (mapObj.current && window.L) {
      const L = window.L;
      userMarker.current.setLatLng([userPosition.lat, userPosition.lng]);

      if (destination) {
        if (!destMarker.current) destMarker.current = L.marker([destination.lat, destination.lon]).addTo(mapObj.current);
        else destMarker.current.setLatLng([destination.lat, destination.lon]);

        const path = [[userPosition.lat, userPosition.lng], [destination.lat, destination.lon]];
        if (!line.current) line.current = L.polyline(path, { color: 'blue' }).addTo(mapObj.current);
        else line.current.setLatLngs(path);
        
        mapObj.current.fitBounds(path, { padding: [50, 50] });
      }
    }
  }, [userPosition, destination]);

  return <div ref={mapRef} style={{ width: '100%', height: '100%' }} />;
};

// --- COMPONENT: Trip Status (With Share Modal) ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts }) => {
  const [time, setTime] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [sentStatus, setSentStatus] = useState({}); // Tracks which contacts were sent link

  useEffect(() => {
    const i = setInterval(() => setTime(Math.floor((new Date() - new Date(tripDetails.startedAt)) / 1000)), 1000);
    return () => clearInterval(i);
  }, [tripDetails]);

  const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

  const handleSendToContact = async (contact) => {
    setSentStatus(prev => ({ ...prev, [contact._id]: 'sending' }));
    try {
        const token = localStorage.getItem('token');
        // Call the new backend endpoint
        await axios.post('http://localhost:5000/api/trips/share', {
            tripId: tripDetails._id,
            contactName: contact.name,
            contactPhone: contact.phone
        }, { headers: { 'x-auth-token': token }});
        
        setSentStatus(prev => ({ ...prev, [contact._id]: 'sent' }));
    } catch (e) {
        alert("Failed to send.");
        setSentStatus(prev => ({ ...prev, [contact._id]: 'error' }));
    }
  };

  return (
    <div className="panel-container">
      <h3 className="status-title">Trip in Progress</h3>
      <div className="timer-display"><p>ELAPSED TIME</p><span>{format(time)}</span></div>
      
      <div className="trip-actions">
        <button className="share-button" onClick={() => setShowModal(true)}>Share Link</button>
        <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div style={{display:'flex', justifyContent:'space-between'}}>
                <h3>Share with Contacts</h3>
                <button onClick={() => setShowModal(false)} style={{background:'none', border:'none', fontSize:'1.5rem', cursor:'pointer'}}>Ã—</button>
            </div>
            {contacts.length === 0 ? <p>No contacts found.</p> : contacts.map(c => (
                <div key={c._id} className="share-item">
                    <div><strong>{c.name}</strong><br/>{c.phone}</div>
                    <button 
                        className="send-btn" 
                        disabled={sentStatus[c._id] === 'sent'}
                        onClick={() => handleSendToContact(c)}
                    >
                        {sentStatus[c._id] === 'sent' ? 'Sent!' : sentStatus[c._id] === 'sending' ? '...' : 'Send'}
                    </button>
                </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

// --- MAIN DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [destination, setDestination] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const socketRef = useRef();

  useEffect(() => {
    // 1. Get Location
    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }));
    
    // 2. Fetch Contacts
    const token = localStorage.getItem('token');
    if(token) axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } }).then(res => setContacts(res.data));

    // 3. Connect Socket
    socketRef.current = io('http://localhost:5000');
    
    return () => socketRef.current.disconnect();
  }, []);

  const handleSearch = async () => {
    const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
    setResults(res.data);
  };

  const startTrip = async () => {
    if (!destination) return alert("Select a destination first!");
    const token = localStorage.getItem('token');
    try {
        // Create trip in DB
        const res = await axios.post('http://localhost:5000/api/trips/start', {
            destination: { name: destination.display_name, lat: destination.lat, lon: destination.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);

        // Start watching location
        navigator.geolocation.watchPosition(p => {
            const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
            setUserPos(coords);
            socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        });
    } catch(e) { alert("Error starting trip"); }
  };

  return (
    <>
      <DashboardStyles />
      <div className="dashboard-layout">
        <div className="map-column">
          <MapView userPosition={userPos} destination={destination} />
        </div>
        <div className="controls-column">
          {isActive ? (
            <TripStatusPanel tripDetails={tripDetails} onEndTrip={() => setIsActive(false)} contacts={contacts} />
          ) : (
            <div className="panel-container">
                <h3>Start a Trip</h3>
                <div className="search-input-group">
                    <input value={searchTerm} onChange={e => setSearchTerm(e.target.value)} placeholder="Where to?" />
                    <button onClick={handleSearch}>Search</button>
                </div>
                {results.length > 0 && (
                    <ul className="results-list">
                        {results.slice(0,5).map(r => (
                            <li key={r.place_id} onClick={() => setDestination({ lat: r.lat, lon: r.lon, display_name: r.display_name })} style={{background: destination?.place_id === r.place_id ? '#e0e7ff' : ''}}>
                                {r.display_name}
                            </li>
                        ))}
                    </ul>
                )}
                <button className="start-trip-button" onClick={startTrip} disabled={!destination}>Start SafeWalk Trip</button>
                
                <div className="contacts-module">
                    <div className="panel-header"><h3>Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
                    {contacts.map(c => (
                        <div key={c._id} className="contact-item">
                            <div className="contact-avatar">{c.name[0]}</div>
                            <div><strong>{c.name}</strong><br/>{c.phone}</div>
                        </div>
                    ))}
                </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
