import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- CONFIG ---
const API_URL = 'http://172.18.24.204:5000';

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f8f9fa; position: relative; font-family: 'Segoe UI', sans-serif; }
    .map-column { flex: 3; background-color: #e9ecef; position: relative; }
    .controls-column { flex: 2; background-color: #f8f9fa; padding: 2rem; display: flex; flex-direction: column; overflow-y: auto; gap: 1.5rem; }
    
    /* Card Component */
    .dashboard-card { background: #ffffff; border-radius: 16px; padding: 1.5rem; box-shadow: 0 4px 20px rgba(0, 0, 0, 0.05); border: 1px solid #f1f3f5; display: flex; flex-direction: column; }
    .card-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1rem; padding-bottom: 0.5rem; border-bottom: 2px solid #f8f9fa; }
    .card-header h3 { margin: 0; font-size: 1.25rem; color: #212529; font-weight: 700; }
    
    /* Manage Link */
    .manage-link { text-decoration: none; color: #007bff; font-weight: 600; font-size: 0.9rem; cursor: pointer; padding: 4px 8px; border-radius: 4px; transition: background 0.2s; }
    .manage-link:hover { background-color: #e7f5ff; }

    /* Inputs & Forms */
    .search-input-group { display: flex; gap: 0.8rem; margin-bottom: 1rem; }
    .search-input-group input { flex-grow: 1; padding: 12px; border: 1px solid #dee2e6; border-radius: 8px; font-size: 1rem; }
    .search-input-group button { background: linear-gradient(135deg, #6f42c1, #512da8); color: white; border: none; border-radius: 8px; padding: 0 1.2rem; cursor: pointer; font-weight: 600; }
    
    .results-section { margin-bottom: 1rem; max-height: 180px; overflow-y: auto; border: 1px solid #e9ecef; border-radius: 8px; }
    .results-list li { padding: 10px 15px; border-bottom: 1px solid #f1f3f5; cursor: pointer; font-size: 0.9rem; color: #495057; }
    .results-list li:hover { background-color: #f8f9fa; color: #b8369a; }

    /* Buttons */
    .start-trip-button { width: 100%; padding: 14px; margin-top: 10px; background: linear-gradient(135deg, #b8369a 0%, #6a11cb 100%); color: white; border: none; border-radius: 10px; font-weight: bold; font-size: 1.1rem; cursor: pointer; box-shadow: 0 4px 15px rgba(184, 54, 154, 0.3); }
    .start-trip-button:disabled { background: #dee2e6; cursor: not-allowed; box-shadow: none; }
    
    /* Incident Report Button */
    .report-issue-btn { background: #dc3545; color: white; border: none; padding: 10px; border-radius: 8px; width: 100%; font-weight: bold; cursor: pointer; margin-top: 10px; }
    .report-instruction { background: #fff3cd; color: #856404; padding: 10px; border-radius: 8px; margin-bottom: 10px; text-align: center; border: 1px solid #ffeeba; }

    /* SOS & Safe Check */
    .sos-btn-floating { position: absolute; bottom: 40px; right: 40px; width: 90px; height: 90px; background: radial-gradient(circle, #ff4d4d 0%, #b30000 100%); border: 5px solid white; border-radius: 50%; color: white; font-weight: 800; font-size: 1.5rem; box-shadow: 0 10px 30px rgba(220, 38, 38, 0.5); cursor: pointer; z-index: 1000; animation: pulse 2s infinite; }
    @keyframes pulse { 0% { box-shadow: 0 0 0 0 rgba(255, 77, 77, 0.7); } 70% { box-shadow: 0 0 0 25px rgba(255, 77, 77, 0); } 100% { box-shadow: 0 0 0 0 rgba(255, 77, 77, 0); } }
    .safe-check-box { background: #fff3cd; border: 2px solid #ffeeba; padding: 20px; border-radius: 12px; margin-bottom: 30px; text-align: center; }
    .safe-timer { font-size: 1.8rem; font-weight: 800; color: #856404; display: block; margin: 5px 0; }
    .btn-im-safe { background: #28a745; color: white; border: none; padding: 8px 20px; border-radius: 50px; font-weight: bold; cursor: pointer; }

    /* Modals */
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.6); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; width: 90%; max-width: 420px; padding: 2rem; border-radius: 16px; box-shadow: 0 20px 60px rgba(0,0,0,0.2); }
    .share-item { display: flex; justify-content: space-between; align-items: center; padding: 12px; border-bottom: 1px solid #f1f3f5; }
    .wa-btn { background: #25D366; color: white; border: none; padding: 8px 14px; border-radius: 6px; cursor: pointer; font-weight: bold; font-size: 0.9rem; }
    
    /* Report Form */
    .form-group { margin-bottom: 15px; }
    .form-group label { display: block; margin-bottom: 5px; font-weight: 600; }
    .form-group select, .form-group textarea { width: 100%; padding: 8px; border: 1px solid #ddd; border-radius: 4px; box-sizing: border-box; }
  `}</style>
);

// --- LOADERS ---
const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const c = document.createElement('link'); c.rel='stylesheet'; c.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(c);
  const s = document.createElement('script'); s.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload=cb; document.head.appendChild(s);
};

// --- MAP VIEW (With Incident & Report Support) ---
const MapView = ({ userPosition, destination, incidents, isReporting, onMapClick }) => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const userMarkerRef = useRef(null);
  const destMarkerRef = useRef(null);
  const polylineRef = useRef(null);
  const incidentsLayerRef = useRef(null);

  useEffect(() => {
    loadLeaflet(() => {
      setTimeout(() => {
          if (window.L && mapContainerRef.current && !mapInstanceRef.current) {
            try {
                const L = window.L;
                const lat = parseFloat(userPosition?.lat) || 26.7606;
                const lng = parseFloat(userPosition?.lng) || 83.3732;
                
                const map = L.map(mapContainerRef.current).setView([lat, lng], 14);
                L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
                
                // User Marker
                const userIcon = new L.Icon({
                     iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
                     iconSize: [25, 41], iconAnchor: [12, 41], popupAnchor: [1, -34],
                     shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png'
                });
                userMarkerRef.current = L.marker([lat, lng], {icon: userIcon}).addTo(map);
                
                // Incident Layer Group
                incidentsLayerRef.current = L.layerGroup().addTo(map);

                // Click Handler for Reporting
                map.on('click', (e) => {
                    // We emit a custom event because React state inside this closure might be stale
                    // But here we rely on the parent checking state or we can use a ref
                    // Simplest way: always callback, parent decides
                    if (onMapClick) onMapClick(e.latlng);
                });

                mapInstanceRef.current = map;
                map.invalidateSize();
            } catch (e) { console.error("Map Init", e); }
          }
      }, 200);
    });
  }, []);

  // Update User/Dest
  useEffect(() => {
    if (mapInstanceRef.current && window.L) {
      const L = window.L;
      const uLat = parseFloat(userPosition?.lat);
      const uLng = parseFloat(userPosition?.lng);
      
      if (!isNaN(uLat) && userMarkerRef.current) {
         userMarkerRef.current.setLatLng([uLat, uLng]);
         if(!destination) mapInstanceRef.current.panTo([uLat, uLng]);
      }

      if (destination && destination.lat) {
          const dLat = parseFloat(destination.lat);
          const dLng = parseFloat(destination.lon);
          if (!isNaN(dLat)) {
             if (!destMarkerRef.current) destMarkerRef.current = L.marker([dLat, dLng]).addTo(mapInstanceRef.current);
             else destMarkerRef.current.setLatLng([dLat, dLng]);

             const path = [[uLat, uLng], [dLat, dLng]];
             if (!polylineRef.current) polylineRef.current = L.polyline(path, { color: '#6f42c1', weight: 4 }).addTo(mapInstanceRef.current);
             else polylineRef.current.setLatLngs(path);
             
             try{ mapInstanceRef.current.fitBounds(path, { padding: [50, 50] }); }catch(e){}
          }
      }
    }
  }, [userPosition, destination]);

  // Update Incidents
  useEffect(() => {
      if (mapInstanceRef.current && incidentsLayerRef.current && window.L) {
          const L = window.L;
          incidentsLayerRef.current.clearLayers();
          
          incidents.forEach(inc => {
              const iconUrl = 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-red.png';
              const icon = new L.Icon({
                  iconUrl: iconUrl,
                  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
                  iconSize: [25, 41], iconAnchor: [12, 41], popupAnchor: [1, -34]
              });
              L.marker([inc.location.lat, inc.location.lng], {icon})
               .bindPopup(`<b>${inc.type.toUpperCase()}</b><br>${inc.description}`)
               .addTo(incidentsLayerRef.current);
          });
      }
  }, [incidents]);

  // Cursor style
  useEffect(() => {
      if(mapInstanceRef.current && mapContainerRef.current) {
          mapContainerRef.current.style.cursor = isReporting ? 'crosshair' : 'grab';
      }
  }, [isReporting]);

  return <div ref={mapContainerRef} style={{ width: '100%', height: '100%' }} />;
};

