// --- TRIP STATUS PANEL (Same) ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts, onSOS, safeCheckSeconds, handleImSafe }) => {
    const [time, setTime] = useState(0);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        if(!tripDetails?.startedAt) return;
        const start = new Date(tripDetails.startedAt);
        const i = setInterval(() => setTime(Math.floor((new Date() - start) / 1000)), 1000);
        return () => clearInterval(i);
    }, [tripDetails]);

    const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);
    const handleWhatsAppShare = (c) => {
        const link = `${window.location.origin}/track/${tripDetails._id}`;
        const msg = `ðŸš¨ I started a SafeWalk trip! Track: ${link}`;
        window.open(`https://api.whatsapp.com/send?phone=${c.phone.replace(/[^0-9]/g,'')}&text=${encodeURIComponent(msg)}`, '_blank');
    };

    return (
        <div className="panel-container">
            <div className="dashboard-card">
                <h3 className="status-title">Trip in Progress</h3>
                {safeCheckSeconds !== null && (
                    <div className="safe-check-box">
                        <small style={{fontWeight:'bold', color:'#856404'}}>SAFE CHECK</small>
                        <span className="safe-timer" style={{color: safeCheckSeconds < 30 ? 'red' : 'inherit'}}>{Math.floor(safeCheckSeconds/60)}:{String(safeCheckSeconds%60).padStart(2,'0')}</span>
                        <button className="btn-im-safe" onClick={handleImSafe}>I'm Safe</button>
                    </div>
                )}
                <div className="timer-display"><p>ELAPSED TIME</p><span>{format(time)}</span></div>
                <div className="trip-actions">
                    <button className="share-button" onClick={() => setShowModal(true)}>Share Link</button>
                    <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
                </div>
            </div>
            <button className="sos-btn-floating" onClick={onSOS}>SOS</button>
            {showModal && <div className="modal-overlay"><div className="modal-content"><h3>Share <button onClick={()=>setShowModal(false)} style={{float:'right',border:'none'}}>x</button></h3>{contacts.map(c=><div key={c._id} className="share-item"><strong>{c.name}</strong><button className="wa-btn" onClick={()=>handleWhatsAppShare(c)}>WhatsApp</button></div>)}</div></div>}
        </div>
    );
};

// --- DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [dest, setDest] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  
  // Safe Check
  const [safeCheckInterval, setSafeCheckInterval] = useState(null);
  const [safeCheckSeconds, setSafeCheckSeconds] = useState(null);
  const [sosCountdown, setSosCountdown] = useState(null);

  // Incident Reporting State
  const [incidents, setIncidents] = useState([]);
  const [isReporting, setIsReporting] = useState(false);
  const [showReportForm, setShowReportForm] = useState(false);
  const [reportCoords, setReportCoords] = useState(null);
  const [repType, setRepType] = useState('harassment');
  const [repDesc, setRepDesc] = useState('');

  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    socketRef.current = io(API_URL);
    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }), console.error, {enableHighAccuracy: true});
    
    const token = localStorage.getItem('token');
    if(!token) { navigate('/login'); return; }
    
    // Fetch Contacts
    axios.get(`${API_URL}/api/contacts`, { headers: { 'x-auth-token': token } })
         .then(res => setContacts(res.data))
         .finally(() => setLoading(false));

    // Fetch Incidents
    axios.get(`${API_URL}/api/incidents`).then(res => setIncidents(res.data));

    return () => {
        if(socketRef.current) socketRef.current.disconnect();
        if(watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, [navigate]);

  // Timer Logic (SOS & SafeCheck) - Same as before ...
  useEffect(() => {
      let t;
      if (isActive && safeCheckSeconds !== null && sosCountdown === null) {
          t = setInterval(() => setSafeCheckSeconds(p => { if(p<=1){triggerSOSSequence(); return parseFloat(safeCheckInterval)*60;} return p-1; }), 1000);
      }
      return () => clearInterval(t);
  }, [isActive, safeCheckSeconds, sosCountdown]);

  useEffect(() => {
      let t;
      if (sosCountdown !== null) {
          if (sosCountdown > 0) t = setInterval(() => setSosCountdown(p=>p-1), 1000);
          else { sendSOS(); setSosCountdown(null); }
      }
      return () => clearInterval(t);
  }, [sosCountdown]);

  // Handlers
  const handleSearch = async () => { try{ const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`); setResults(res.data); }catch(e){} };
  const triggerSOSSequence = () => setSosCountdown(5);
  const sendSOS = async () => { /* SOS Logic */ if(tripDetails) socketRef.current.emit('sosTriggered', tripDetails._id); alert("SOS SENT"); };
  const handleImSafe = async () => { setSafeCheckSeconds(parseFloat(safeCheckInterval)*60); socketRef.current.emit('sosCancelled', tripDetails._id); alert("Marked Safe"); };
  
  const startTrip = async () => {
    if(!dest) return alert("Select Dest");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post(`${API_URL}/api/trips/start`, { destination: { name: dest.display_name, lat: dest.lat, lon: dest.lon } }, { headers: { 'x-auth-token': token } });
        setTripDetails(res.data); setIsActive(true); if(safeCheckInterval) setSafeCheckSeconds(parseFloat(safeCheckInterval)*60);
        socketRef.current.emit('joinTripRoom', res.data._id);
        watchIdRef.current = navigator.geolocation.watchPosition(p => {
             const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
             setUserPos(coords);
             socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        }, console.error, { enableHighAccuracy: true });
    } catch(e) { alert("Error starting"); }
  };
  
  const endTrip = async () => {
    if(watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    socketRef.current.emit('endTrip', tripDetails._id);
    const token = localStorage.getItem('token');
    await axios.post(`${API_URL}/api/trips/${tripDetails._id}/end`, {}, { headers: { 'x-auth-token': token } });
    setIsActive(false); setTripDetails(null); setDest(null); setResults([]); alert("Trip Ended");
  };

  // --- REPORT INCIDENT HANDLERS ---
  const handleReportStart = () => { setIsReporting(true); alert("Click on the map where the incident happened."); };
  
  const handleMapClick = (latlng) => {
      if(isReporting) {
          setReportCoords(latlng);
          setShowReportForm(true);
          setIsReporting(false);
      }
  };

  const submitReport = async () => {
      const token = localStorage.getItem('token');
      try {
          await axios.post(`${API_URL}/api/incidents`, { type: repType, description: repDesc, lat: reportCoords.lat, lng: reportCoords.lng }, { headers: { 'x-auth-token': token } });
          alert("Report Submitted!"); setShowReportForm(false); setRepDesc('');
          const res = await axios.get(`${API_URL}/api/incidents`); setIncidents(res.data);
      } catch(e) { alert("Failed to submit"); }
  };

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column">
          <MapView userPosition={userPos} destination={dest} incidents={incidents} isReporting={isReporting} onMapClick={handleMapClick} />
          {isReporting && <div style={{position:'absolute', top:20, left:'50%', transform:'translateX(-50%)', background:'rgba(0,0,0,0.8)', color:'white', padding:'10px 20px', borderRadius:'20px', zIndex:1000}}>Click Map to Report</div>}
      </div>
      
      <div className="controls-column">
        {isActive ? (
          <TripStatusPanel tripDetails={tripDetails} onEndTrip={endTrip} contacts={contacts} onSOS={triggerSOSSequence} safeCheckSeconds={safeCheckSeconds} handleImSafe={handleImSafe} />
        ) : (
          <BeforeTripPanel 
             contacts={contacts} loading={loading} searchTerm={searchTerm} setSearchTerm={setSearchTerm} 
             onSearch={handleSearch} results={results} onSelectDest={setDest} dest={dest} onStart={startTrip} 
             setSafeCheckInterval={setSafeCheckInterval} onReportStart={handleReportStart}
          />
        )}
      </div>

      {/* SOS Countdown */}
      {sosCountdown !== null && (
          <div className="sos-countdown-overlay"><h1>SENDING SOS...</h1><div className="countdown-number">{sosCountdown}</div><button className="btn-cancel-sos" onClick={()=>setSosCountdown(null)}>CANCEL</button></div>
      )}

      {/* Report Form Modal */}
      {showReportForm && (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>Report Incident</h3>
                <div style={{marginBottom:'10px'}}>
                    <label>Type:</label>
                    <select style={{width:'100%', padding:'8px'}} value={repType} onChange={e=>setRepType(e.target.value)}>
                        <option value="harassment">Harassment</option>
                        <option value="lighting">Poor Lighting</option>
                        <option value="theft">Theft</option>
                        <option value="other">Other</option>
                    </select>
                </div>
                <div style={{marginBottom:'10px'}}>
                    <label>Description:</label>
                    <textarea rows="3" style={{width:'100%', padding:'8px'}} value={repDesc} onChange={e=>setRepDesc(e.target.value)}></textarea>
                </div>
                <button style={{width:'100%', padding:'10px', background:'#dc3545', color:'white', border:'none', borderRadius:'5px'}} onClick={submitReport}>Submit</button>
                <button style={{width:'100%', padding:'10px', marginTop:'10px', background:'#eee', border:'none', borderRadius:'5px'}} onClick={()=>setShowReportForm(false)}>Cancel</button>
            </div>
        </div>
      )}
    </div>
  );
};
export default Dashboard;
