import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- CONFIG ---
const API_URL = 'http://172.18.24.204:5000'; // Your IP

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; position: relative; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05); display: flex; flex-direction: column; overflow-y: hidden; }
    
    /* SOS Button (Floating) */
    .sos-btn-floating {
        position: absolute; bottom: 30px; right: 30px; width: 80px; height: 80px;
        background: radial-gradient(circle, #ff4d4d 0%, #cc0000 100%);
        border: 4px solid white; border-radius: 50%; color: white; font-weight: bold; font-size: 1.2rem;
        box-shadow: 0 4px 15px rgba(255, 0, 0, 0.4); cursor: pointer; z-index: 1000;
        animation: pulse 2s infinite;
    }
    @keyframes pulse { 0% { transform: scale(1); box-shadow: 0 0 0 0 rgba(255, 0, 0, 0.7); } 70% { transform: scale(1.1); box-shadow: 0 0 0 20px rgba(255, 0, 0, 0); } 100% { transform: scale(1); box-shadow: 0 0 0 0 rgba(255, 0, 0, 0); } }

    /* Safe Check Panel */
    .safe-check-box { background: #fff3cd; border: 1px solid #ffeeba; padding: 15px; border-radius: 8px; margin-bottom: 20px; text-align: center; }
    .safe-timer { font-size: 1.5rem; font-weight: bold; color: #856404; display: block; margin: 10px 0; }
    .btn-im-safe { background: #28a745; color: white; border: none; padding: 8px 16px; border-radius: 4px; font-weight: bold; cursor: pointer; }

    /* Standard Styles */
    .panel-container { display: flex; flex-direction: column; height: 100%; }
    .panel-header { display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid #e5e7eb; padding-bottom: 1rem; margin-bottom: 1rem; }
    .panel-header h3 { margin: 0; font-size: 1.25rem; color: #111827; }
    .search-input-group { display: flex; gap: 0.5rem; margin-bottom: 1rem; }
    .search-input-group input { flex-grow: 1; padding: 0.75rem; border: 1px solid #d1d5db; border-radius: 8px; font-size: 1rem; }
    .search-input-group button { background: linear-gradient(90deg, #b8369a, #6a11cb); color: white; border: none; border-radius: 8px; padding: 0 1rem; cursor: pointer; }
    .results-list { list-style: none; padding: 0; margin: 0; border: 1px solid #e5e7eb; border-radius: 8px; max-height: 150px; overflow-y: auto; }
    .results-list li { padding: 0.75rem 1rem; border-bottom: 1px solid #e5e7eb; cursor: pointer; font-size: 0.9rem; }
    .results-list li:hover { background-color: #f3f4f6; }
    .start-trip-button { width: 100%; padding: 1rem; margin-top: 1rem; font-size: 1.1rem; font-weight: bold; color: white; background: linear-gradient(90deg, #b669a4, #8d5ec1); border: none; border-radius: 8px; cursor: pointer; }
    .start-trip-button:disabled { background: #c4b5fd; cursor: not-allowed; }
    .contacts-module { flex-grow: 1; margin-top: 2rem; overflow-y: auto; }
    .contact-item { display: flex; align-items: center; padding: 0.75rem 0; gap: 1rem; border-bottom: 1px solid #f3f4f6; }
    .contact-avatar { width: 40px; height: 40px; border-radius: 50%; background-color: #e0e7ff; color: #4338ca; display: grid; place-items: center; font-weight: bold; }
    .status-title { text-align: center; font-size: 1.5rem; margin-bottom: 1rem; }
    .timer-display { text-align: center; margin: 2rem 0; }
    .timer-display span { font-size: 3rem; font-weight: bold; color: #111; }
    .trip-actions { display: flex; gap: 1rem; margin-top: auto; }
    .share-button { flex: 1; padding: 1rem; background: #3b82f6; color: white; border: none; border-radius: 8px; cursor: pointer; font-weight: bold; }
    .end-trip-button { flex: 1; padding: 1rem; background: #ef4444; color: white; border: none; border-radius: 8px; cursor: pointer; font-weight: bold; }
    
    /* Modal */
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; width: 90%; max-width: 400px; padding: 2rem; border-radius: 12px; }
    .wa-btn { background: #25D366; color: white; border: none; padding: 8px 12px; border-radius: 6px; cursor: pointer; font-weight: bold; font-size: 0.9rem; text-decoration: none; }
  `}</style>
);

// --- LOADERS ---
const loadLeafletCSS = () => { if (!document.getElementById('leaflet-css')) { const l = document.createElement('link'); l.id = 'leaflet-css'; l.rel = 'stylesheet'; l.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(l); } };
const loadLeafletScript = (cb) => { if (window.L) return cb(); const s = document.createElement('script'); s.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload = () => cb(); document.head.appendChild(s); };

// --- MAP VIEW ---
const MapView = ({ userPosition, destination }) => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const userMarkerRef = useRef(null);
  const destMarkerRef = useRef(null);
  const polylineRef = useRef(null);

  useEffect(() => {
    loadLeafletCSS();
    loadLeafletScript(() => {
      const L = window.L;
      if (mapContainerRef.current && !mapInstanceRef.current) {
        const uLat = parseFloat(userPosition.lat) || 26.76;
        const uLng = parseFloat(userPosition.lng) || 83.37;
        mapInstanceRef.current = L.map(mapContainerRef.current).setView([uLat, uLng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstanceRef.current);
        userMarkerRef.current = L.marker([uLat, uLng]).addTo(mapInstanceRef.current);
      }
    });
  }, []);

  useEffect(() => {
    if (mapInstanceRef.current && window.L) {
      const L = window.L;
      const uLat = parseFloat(userPosition.lat);
      const uLng = parseFloat(userPosition.lng);

      if(userMarkerRef.current) userMarkerRef.current.setLatLng([uLat, uLng]);
      
      if (destination && destination.lat) {
        const dLat = parseFloat(destination.lat);
        const dLng = parseFloat(destination.lon);
        if (!destMarkerRef.current) destMarkerRef.current = L.marker([dLat, dLng]).addTo(mapInstanceRef.current);
        else destMarkerRef.current.setLatLng([dLat, dLng]);

        const path = [[uLat, uLng], [dLat, dLng]];
        if (!polylineRef.current) polylineRef.current = L.polyline(path, { color: '#4f46e5' }).addTo(mapInstanceRef.current);
        else polylineRef.current.setLatLngs(path);
        
        mapInstanceRef.current.fitBounds(path, { padding: [50, 50] });
      }
    }
  }, [userPosition, destination]);

  return <div ref={mapContainerRef} style={{ width: '100%', height: '100%' }} />;
};

// --- BEFORE TRIP PANEL ---
const BeforeTripPanel = ({ contacts, searchTerm, setSearchTerm, onSearch, results, onSelectDest, dest, onStart, searchError, setSafeCheckInterval }) => (
    <div className="panel-container">
        <div className="trip-planning-module">
          <h3>Start a New Trip</h3>
          <div className="destination-form">
            <label>Destination</label>
            <div className="search-input-group">
              <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              <button onClick={onSearch}>Search</button>
            </div>
            {searchError && <p style={{color:'red'}}>{searchError}</p>}
            {results.length > 0 && (
              <ul className="results-list">
                  {results.slice(0, 3).map((r) => (
                    <li key={r.place_id} onClick={() => onSelectDest({lat: r.lat, lon: r.lon, display_name: r.display_name})} style={{background: dest?.place_id === r.place_id ? '#e0e7ff':''}}>
                      {r.display_name}
                    </li>
                  ))}
              </ul>
            )}
            
            {/* Safe Check Selector */}
            <div style={{margin: '10px 0'}}>
                <label>Safe Check Interval:</label>
                <select style={{width:'100%', padding:'10px'}} onChange={(e) => setSafeCheckInterval(e.target.value)}>
                    <option value="">Off</option>
                    <option value="1">1 Minute (Test)</option>
                    <option value="15">15 Minutes</option>
                    <option value="30">30 Minutes</option>
                </select>
            </div>

            <button className="start-trip-button" onClick={onStart} disabled={!dest}>Start SafeWalk Trip</button>
          </div>
        </div>
        <div className="contacts-module">
          <div className="panel-header"><h3>Trusted Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
          <div className="contact-list">
            {contacts.map(c => (
              <div key={c._id} className="contact-item">
                <div className="contact-avatar">{c.name.charAt(0)}</div>
                <div><strong>{c.name}</strong><br/><small>{c.phone}</small></div>
              </div>
            ))}
          </div>
        </div>
    </div>
);

// --- TRIP STATUS PANEL (With Safe Check & SOS) ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts, onSOS, safeCheckSeconds, resetSafeCheck }) => {
    const [time, setTime] = useState(0);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const i = setInterval(() => setTime(Math.floor((new Date() - new Date(tripDetails.startedAt)) / 1000)), 1000);
        return () => clearInterval(i);
    }, [tripDetails]);

    const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

    const handleWhatsAppShare = (contact) => {
        const link = `${window.location.origin}/track/${tripDetails._id}`;
        const msg = `ðŸš¨ SafeWalk: Tracking Link: ${link}`;
        const phone = contact.phone.replace(/[^0-9]/g, ''); 
        window.open(`https://api.whatsapp.com/send?phone=${phone}&text=${encodeURIComponent(msg)}`, '_blank');
    };

    return (
        <div className="panel-container">
            <h3 className="status-title">Trip in Progress</h3>
            
            {/* SAFE CHECK TIMER */}
            {safeCheckSeconds !== null && (
                <div className="safe-check-box">
                    <small>SAFE CHECK IN</small>
                    <span className="safe-timer" style={{color: safeCheckSeconds < 30 ? 'red' : 'inherit'}}>
                        {Math.floor(safeCheckSeconds / 60)}:{String(safeCheckSeconds % 60).padStart(2,'0')}
                    </span>
                    <button className="btn-im-safe" onClick={resetSafeCheck}>I'm Safe</button>
                </div>
            )}

            <div className="timer-display"><p>TRIP DURATION</p><span>{format(time)}</span></div>
            
            <div className="trip-actions">
                <button className="share-button" onClick={() => setShowModal(true)}>Share</button>
                <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
            </div>

            {/* SOS BUTTON - Always Visible */}
            <button className="sos-btn-floating" onClick={onSOS}>SOS</button>

            {showModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Share with... <button onClick={() => setShowModal(false)} style={{float:'right'}}>x</button></h3>
                        {contacts.map(c => (
                            <div key={c._id} className="share-item">
                                <div><strong>{c.name}</strong></div>
                                <button className="wa-btn" onClick={() => handleWhatsAppShare(c)}>WhatsApp</button>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

// --- MAIN DASHBOARD ---
const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [destination, setDestination] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const [searchError, setSearchError] = useState('');
  
  // Safe Check State
  const [safeCheckInterval, setSafeCheckInterval] = useState(null); // Minutes
  const [safeCheckSeconds, setSafeCheckSeconds] = useState(null); // Countdown
  
  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();
  
  // 1. Initial Load
  useEffect(() => {
    socketRef.current = io(API_URL);

    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }));
    
    const token = localStorage.getItem('token');
    if(!token) { navigate('/login'); return; }
    
    axios.get(`${API_URL}/api/contacts`, { headers: { 'x-auth-token': token } })
         .then(res => setContacts(res.data))
         .catch(e => console.error(e));

    return () => {
      if(socketRef.current) socketRef.current.disconnect();
      if(watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, [navigate]);

  // 2. Safe Check Logic (The "Dead Man Switch")
  useEffect(() => {
      let timer;
      if (isActive && safeCheckSeconds !== null) {
          timer = setInterval(() => {
              setSafeCheckSeconds(prev => {
                  if (prev <= 1) {
                      handleSOS(); // AUTO TRIGGER SOS
                      return safeCheckInterval * 60; // Reset
                  }
                  return prev - 1;
              });
          }, 1000);
      }
      return () => clearInterval(timer);
  }, [isActive, safeCheckSeconds]); // Depend on seconds to update

  const handleSearch = async () => {
    try {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      setResults(res.data);
    } catch(e) { setSearchError("Search failed."); }
  };

  const handleStartTrip = async () => {
    if (!destination) return alert("Select a destination!");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post(`${API_URL}/api/trips/start`, {
            destination: { name: destination.display_name, lat: destination.lat, lon: destination.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);
        
        // Init Safe Check Timer
        if (safeCheckInterval) {
            setSafeCheckSeconds(parseInt(safeCheckInterval) * 60);
        } else {
            setSafeCheckSeconds(null);
        }

        socketRef.current.emit('joinTripRoom', res.data._id);
        watchIdRef.current = navigator.geolocation.watchPosition(p => {
             const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
             setUserPos(coords);
             socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        });
    } catch(e) { alert("Error starting trip."); }
  };

  const handleEndTrip = async () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    const token = localStorage.getItem('token');
    
    if(tripDetails) {
        socketRef.current.emit('endTrip', tripDetails._id);
        await axios.post(`${API_URL}/api/trips/${tripDetails._id}/end`, {}, { headers: { 'x-auth-token': token } });
    }

    setIsActive(false);
    setTripDetails(null);
    setDestination(null);
    setSearchTerm('');
    setResults([]);
    alert("Trip ended safely.");
  };

  // --- SOS LOGIC ---
  const handleSOS = async () => {
      // 1. Visual Alert
      alert("ðŸš¨ SOS TRIGGERED! Sending alerts to all contacts.");
      
      // 2. Notify Backend
      if(tripDetails) {
          const token = localStorage.getItem('token');
          // Call SOS API
          await axios.post(`${API_URL}/api/trips/${tripDetails._id}/sos`, {
              lat: userPos.lat, lng: userPos.lng
          }, { headers: { 'x-auth-token': token } });
          
          // Emit Socket Event for instant popup on family's screen
          socketRef.current.emit('notifyContacts', {
              tripId: tripDetails._id,
              senderName: "USER (SOS)", // Replace with real name if avail
              message: "ðŸš¨ SOS! I need help immediately!"
          });
      }
      
      // 3. Open WhatsApp for ALL contacts (Browser restriction: might block popups)
      contacts.forEach(c => {
         const link = `${window.location.origin}/track/${tripDetails?._id}`;
         const msg = `ðŸš¨ SOS! I NEED HELP! Track me: ${link}`;
         const url = `https://api.whatsapp.com/send?phone=${c.phone}&text=${encodeURIComponent(msg)}`;
         window.open(url, '_blank');
      });
  };

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column"><MapView userPosition={userPos} destination={destination} /></div>
      <div className="controls-column">
        {isActive ? (
          <TripStatusPanel 
             tripDetails={tripDetails} 
             onEndTrip={handleEndTrip} 
             contacts={contacts} 
             onSOS={handleSOS} // Pass SOS handler
             safeCheckSeconds={safeCheckSeconds}
             resetSafeCheck={() => setSafeCheckSeconds(safeCheckInterval * 60)}
          />
        ) : (
          <BeforeTripPanel
            contacts={contacts} loading={false} searchTerm={searchTerm}
            setSearchTerm={setSearchTerm} onSearch={handleSearch} results={results}
            onSelectDest={setDestination} dest={destination} onStart={handleStartTrip} searchError={searchError}
            setSafeCheckInterval={setSafeCheckInterval} // Pass timer selector
          />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
