import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// ... (Your DashboardStyles, loadLeaflet functions, MapView, BeforeTripPanel remain the same) ...
// TO SAVE SPACE, I AM ONLY SHOWING THE PARTS THAT CHANGED. 
// KEEP YOUR EXISTING SUB-COMPONENTS IF NOT SHOWN HERE.

// --- STYLES (Self-Contained for Stability) ---
const DashboardStyles = () => (
  <style>{`
    /* ... (Same styles as before) ... */
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; position: relative; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05); display: flex; flex-direction: column; overflow-y: hidden; }
    .panel-container { display: flex; flex-direction: column; height: 100%; position: relative; }
    .panel-header { display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid #e5e7eb; padding-bottom: 1rem; margin-bottom: 1rem; }
    .panel-header h3 { margin: 0; font-size: 1.25rem; color: #111827; }
    .trip-planning-module { flex-shrink: 0; }
    .destination-form label { font-weight: 600; color: #374151; display: block; margin-bottom: 0.5rem; }
    .search-input-group { display: flex; gap: 0.5rem; margin-bottom: 1rem; }
    .search-input-group input { flex-grow: 1; padding: 0.75rem; border: 1px solid #d1d5db; border-radius: 8px; font-size: 1rem; }
    .search-input-group button { background: linear-gradient(90deg, #b8369a, #6a11cb); color: white; border: none; border-radius: 8px; padding: 0 1rem; cursor: pointer; }
    .results-section { margin-bottom: 1rem; max-height: 150px; overflow-y: auto; }
    .results-list { list-style: none; padding: 0; margin: 0; border: 1px solid #e5e7eb; border-radius: 8px; }
    .results-list li { padding: 0.75rem 1rem; border-bottom: 1px solid #e5e7eb; cursor: pointer; font-size: 0.9rem; }
    .results-list li:last-child { border-bottom: none; }
    .results-list li:hover { background-color: #f3f4f6; }
    .results-list li.selected { background-color: #e0e7ff; font-weight: bold; }
    .start-trip-button { width: 100%; padding: 1rem; margin-top: 1rem; font-size: 1.1rem; font-weight: bold; color: white; background: linear-gradient(90deg, #b669a4, #8d5ec1); border: none; border-radius: 8px; cursor: pointer; }
    .start-trip-button:disabled { background: #c4b5fd; cursor: not-allowed; }
    .search-error, .error-text { color: #ef4444; }
    .contacts-module { flex-grow: 1; margin-top: 2rem; overflow-y: auto; }
    .manage-link { text-decoration: none; color: #4f46e5; font-weight: 600; }
    .contact-item { display: flex; align-items: center; padding: 0.75rem 0; gap: 1rem; }
    .contact-avatar { width: 40px; height: 40px; border-radius: 50%; background-color: #e0e7ff; color: #4338ca; display: grid; place-items: center; font-weight: bold; }
    .contact-details { display: flex; flex-direction: column; }
    .status-title { text-align: center; font-size: 1.5rem; }
    .timer-display { text-align: center; margin: auto; }
    .timer-display p { margin: 0; color: #6b7280; }
    .timer-display span { font-size: 4rem; font-weight: bold; }
    .trip-actions { display: flex; gap: 1rem; margin-top: 2rem; }
    .share-button { background-color: #f3f4f6; color: #111; flex: 1; padding: 1rem; font-weight: bold; border-radius: 8px; cursor: pointer; border: none; }
    .end-trip-button { background-color: #ef4444; color: white; flex: 1; padding: 1rem; font-weight: bold; border-radius: 8px; cursor: pointer; border: none; }
    /* Modal Styles */
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; width: 90%; max-width: 400px; padding: 2rem; border-radius: 12px; }
    .modal-header { display: flex; justify-content: space-between; margin-bottom: 1.5rem; }
    .share-list { display: flex; flex-direction: column; gap: 1rem; }
    .share-item { display: flex; justify-content: space-between; align-items: center; padding: 0.5rem; border: 1px solid #eee; border-radius: 8px; }
    .send-btn { background: #4f46e5; color: white; border: none; padding: 0.5rem 1rem; border-radius: 6px; cursor: pointer; }
    .send-btn:disabled { background: #a5b4fc; cursor: default; }
  `}</style>
);

const loadLeafletCSS = () => {
  if (!document.getElementById('leaflet-css')) {
    const link = document.createElement('link'); link.id = 'leaflet-css'; link.rel = 'stylesheet'; link.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(link);
  }
};
const loadLeafletScript = (callback) => {
  if (window.L) { callback(); return; }
  const script = document.createElement('script'); script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; script.onload = () => callback(); document.head.appendChild(script);
};

// ... MapView Component (Keep as is) ...
const MapView = ({ userPosition, destination }) => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const userMarkerRef = useRef(null);
  const destMarkerRef = useRef(null);
  const polylineRef = useRef(null);

  useEffect(() => {
    if (mapContainerRef.current && window.L) {
      const L = window.L;
      if (!mapInstanceRef.current) {
        mapInstanceRef.current = L.map(mapContainerRef.current).setView([userPosition.lat, userPosition.lng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstanceRef.current);
      }
      if (!userMarkerRef.current) {
        userMarkerRef.current = L.marker([userPosition.lat, userPosition.lng]).addTo(mapInstanceRef.current);
      } else {
        userMarkerRef.current.setLatLng([userPosition.lat, userPosition.lng]);
      }
      if (destination) {
        if (!destMarkerRef.current) destMarkerRef.current = L.marker([destination.lat, destination.lon]).addTo(mapInstanceRef.current);
        else destMarkerRef.current.setLatLng([destination.lat, destination.lon]);
        
        const latlngs = [[userPosition.lat, userPosition.lng], [destination.lat, destination.lon]];
        if (!polylineRef.current) polylineRef.current = L.polyline(latlngs, { color: '#4f46e5' }).addTo(mapInstanceRef.current);
        else polylineRef.current.setLatLngs(latlngs);
        mapInstanceRef.current.fitBounds(latlngs, { padding: [50, 50] });
      } else if (polylineRef.current) {
        polylineRef.current.remove(); polylineRef.current = null;
        mapInstanceRef.current.setView([userPosition.lat, userPosition.lng], 13);
      }
    }
  }, [userPosition, destination]);
  return <div ref={mapContainerRef} style={{ width: '100%', height: '100%' }} />;
};

// ... BeforeTripPanel (Keep as is) ...
const BeforeTripPanel = ({ contacts, loading, error, searchTerm, setSearchTerm, onSearch, isSearching, searchResults, onSelectDestination, selectedDestination, onStartTrip, searchError }) => (
    <div className="panel-container">
        <div className="trip-planning-module">
          <h3>Start a New Trip</h3>
          <div className="destination-form">
            <label>Where are you going?</label>
            <div className="search-input-group">
              <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && onSearch()} />
              <button onClick={onSearch} disabled={isSearching}>{isSearching ? '...' : 'Search'}</button>
            </div>
            {searchError && <p className="search-error">{searchError}</p>}
            {searchResults.length > 0 && (
              <div className="results-section">
                <ul className="results-list">
                  {searchResults.slice(0, 5).map((result) => (
                    <li key={result.place_id} className={selectedDestination?.place_id === result.place_id ? 'selected' : ''} onClick={() => onSelectDestination(result)}>
                      {result.display_name}
                    </li>
                  ))}
                </ul>
              </div>
            )}
            <button className="start-trip-button" onClick={onStartTrip} disabled={!selectedDestination}>Start SafeWalk Trip</button>
          </div>
        </div>
        <div className="contacts-module">
          <div className="panel-header"><h3>Trusted Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
          <div className="contact-list">
            {loading && <p>Loading contacts...</p>}
            {error && <p className="error-text">{error}</p>}
            {!loading && !error && contacts.map(c => (
              <div key={c._id} className="contact-item">
                <div className="contact-avatar">{c.name.charAt(0)}</div>
                <div className="contact-details"><strong>{c.name}</strong><span>{c.phone}</span></div>
              </div>
            ))}
          </div>
        </div>
    </div>
);

// --- UPDATED TRIP STATUS PANEL ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts }) => {
    const [elapsedTime, setElapsedTime] = useState(0);
    const [showShareModal, setShowShareModal] = useState(false);
    const [sentStatus, setSentStatus] = useState({}); 
    // --- SOCKET REF FOR NOTIFICATIONS ---
    const socketRef = useRef(null);

    useEffect(() => {
        const timer = setInterval(() => {
            setElapsedTime(Math.floor((new Date() - new Date(tripDetails.startedAt)) / 1000));
        }, 1000);
        
        // Connect socket for notifications
        socketRef.current = io('http://localhost:5000');
        
        return () => {
            clearInterval(timer);
            if (socketRef.current) socketRef.current.disconnect();
        };
    }, [tripDetails]);

    const formatTime = (s) => `${String(Math.floor(s/3600)).padStart(2,'0')}:${String(Math.floor((s%3600)/60)).padStart(2,'0')}:${String(s%60).padStart(2,'0')}`;

    const handleSendToContact = (contact) => {
        setSentStatus(prev => ({ ...prev, [contact._id]: 'sending' }));
        
        // --- ACTION: Emit the notification event ---
        const token = localStorage.getItem('token');
        if (token) {
            // Decoding token simply to get ID (in real app, verify properly)
            // Or just send the event, server handles logic
             try {
                // We send a notification event to the server
                // The server will then broadcast 'receiveNotification' to the contact if online
                // Note: For this to work perfectly, the contact needs a User ID. 
                // Since our contacts model is simple (name/phone), we simulate by 
                // broadcasting to everyone or using the phone number if registered.
                // For this demo, we emit 'notifyContacts' which the server handles.
                
                // We need the current user's ID to send with the request.
                // Ideally this comes from context, but we decode from token here for simplicity.
                const base64Url = token.split('.')[1];
                const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
                const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
                    return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
                }).join(''));
                const payload = JSON.parse(jsonPayload);
                
                socketRef.current.emit('notifyContacts', { 
                    tripId: tripDetails._id, 
                    userId: payload.user.id 
                });
                
                setSentStatus(prev => ({ ...prev, [contact._id]: 'sent' }));
                
                // Fallback: Also copy link
                const link = `${window.location.origin}/track/${tripDetails._id}`;
                navigator.clipboard.writeText(link);
                
             } catch(e) {
                 console.error(e);
                 setSentStatus(prev => ({ ...prev, [contact._id]: 'error' }));
             }
        }
    };

    return (
        <div className="panel-container">
            <h3 className="status-title">Trip in Progress</h3>
            <div className="timer-display"><p>ELAPSED TIME</p><span>{formatTime(elapsedTime)}</span></div>
            <div className="trip-actions">
                <button className="share-button" onClick={() => setShowShareModal(true)}>Share Link</button>
                <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
            </div>
             {showShareModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <div className="modal-header"><h3>Share</h3><button onClick={() => setShowShareModal(false)}>X</button></div>
                        <div className="share-list">
                            {contacts.length === 0 ? <p>No contacts found.</p> : contacts.map(c => (
                                <div key={c._id} className="share-item">
                                    <div><strong>{c.name}</strong><br/>{c.phone}</div>
                                    <button 
                                        className="send-btn" 
                                        disabled={sentStatus[c._id] === 'sent'}
                                        onClick={() => handleSendToContact(c)}
                                    >
                                        {sentStatus[c._id] === 'sent' ? 'Sent!' : 'Send'}
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [destination, setDestination] = useState(null);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPos, setUserPos] = useState({ lat: 26.76, lng: 83.37 });
  const [error, setError] = useState('');
  const socketRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    loadLeafletCSS();
    loadLeafletScript(() => {});
    
    socketRef.current = io('http://localhost:5000');
    
    // Register user on socket connection
    const token = localStorage.getItem('token');
    if (token) {
        try {
             const base64Url = token.split('.')[1];
             const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
             const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
                return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
             }).join(''));
             const payload = JSON.parse(jsonPayload);
             socketRef.current.emit('registerUser', payload.user.id);
        } catch(e) {}
    }

    navigator.geolocation.getCurrentPosition(p => setUserPos({ lat: p.coords.latitude, lng: p.coords.longitude }));
    
    const fetchContacts = async () => {
        if(!token) { navigate('/login'); return; }
        try {
            const res = await axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } });
            setContacts(res.data);
        } catch (e) { setError('Failed to load contacts.'); } 
        finally { setLoading(false); }
    };
    fetchContacts();

    return () => socketRef.current.disconnect();
  }, [navigate]);

  const handleSearch = async () => {
    const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
    setResults(res.data);
  };

  const startTrip = async () => {
    if (!destination) return alert("Select a destination first!");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post('http://localhost:5000/api/trips/start', {
            destination: { name: destination.display_name, lat: destination.lat, lon: destination.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);

        navigator.geolocation.watchPosition(p => {
            const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
            setUserPos(coords);
            socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        });
    } catch(e) { alert("Error starting trip"); }
  };

  return (
    <>
      <DashboardStyles />
      <div className="dashboard-layout">
        <div className="map-column">
          <MapView userPosition={userPos} destination={destination} />
        </div>
        <div className="controls-column">
          {isActive ? (
            <TripStatusPanel tripDetails={tripDetails} onEndTrip={() => setIsActive(false)} contacts={contacts} />
          ) : (
            <BeforeTripPanel
              contacts={contacts} loading={loading} error={error} searchTerm={searchTerm}
              setSearchTerm={setSearchTerm} onSearch={handleSearch}
              searchResults={results} onSelectDestination={setDestination}
              selectedDestination={destination} onStartTrip={startTrip}
            />
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
