import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Import Router here
import './App.css';

// Pages
import Header from './HomePage/Header'; 
import Footer from './HomePage/Footer';
import Herosection from './HomePage/Herosection';
import Challenges from './HomePage/Challenges';
import Solution from './HomePage/Solution';
import HowItWorks from './HomePage/HowItWorks';
import CTA from './HomePage/CTA';
import Signup from './Pages/Signup';
import Login from './Pages/Login';
import Logout from './Pages/Logout';
import Dashboard from './Pages/Dashboard';
import ManageContacts from './Pages/ManageContacts';
import TrackTripPage from './Pages/TrackTripPage';
import CommunityMap from './Pages/CommunityMap';

// Components
import PrivateRoute from './components/PrivateRoute';
import NotificationListener from './components/NotificationListener';
import Navbar from './components/Navbar';

const LandingPage = () => (
  <>
    <Header />
    <Herosection />
    <Challenges />
    <Solution />
    <HowItWorks />
    <CTA />
  </>
);

function App() {
  return (
    // ✅ FIX: Router must wrap EVERYTHING (Navbar, Listener, and Routes)
    <Router>
      <div className="App">
        
        {/* Global Listener for SOS (Must be inside Router) */}
        <NotificationListener />

        {/* Navbar (Must be inside Router to use Links) */}
        <Navbar />

        <main>
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<LandingPage />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/logout" element={<Logout />} />
            
            {/* Tracking Page - Publicly accessible */}
            <Route path="/track/:tripId" element={<TrackTripPage />} />

            {/* Protected Routes */}
            <Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />
            <Route path="/contacts" element={<PrivateRoute><ManageContacts /></PrivateRoute>} />
            <Route path="/community-map" element={<PrivateRoute><CommunityMap /></PrivateRoute>} />
          </Routes>
        </main>
        
        <Footer />
      </div>
    </Router>
  );
}

export default App;
