import React from 'react';
import { Navigate } from 'react-router-dom';

const PrivateRoute = ({ children }) => {
  // Check if a user's token exists in local storage
  const isAuthenticated = localStorage.getItem('token');

  // If the user is logged in (a token exists), this component will show the
  // page they were trying to access (represented by `children`).
  // If they are not logged in, it will automatically redirect them to the /login page.
  return isAuthenticated ? children : <Navigate to="/login" />;
};

export default PrivateRoute;
