import React, { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import "./style/Header.css";

const getAuthToken = () => localStorage.getItem("token");
const clearAuthToken = () => localStorage.removeItem("token");

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const token = getAuthToken();
    setIsLoggedIn(!!token);
  }, [location]);

  const handleLogout = () => {
    clearAuthToken();
    setIsLoggedIn(false);
    navigate("/");
    setMenuOpen(false);
  };

  const closeMenu = () => setMenuOpen(false);

  return (
    <>
      <header className="header">
        <Link to="/" className="logo">
          SheSecure
        </Link>

        {/* Desktop Navigation */}
        <nav className="nav-links desktop-nav">
          {isLoggedIn ? (
            <>
              <Link to="/dashboard"
                className={location.pathname === "/dashboard" ? "nav-item-active" : ""}>
                Dashboard
              </Link>
              <Link to="/community-map"
                className={location.pathname === "/community-map" ? "nav-item-active" : ""}>
                Community Map
              </Link>
              <Link to="/contacts"
                className={location.pathname === "/contacts" ? "nav-item-active" : ""}>
                Contacts
              </Link>
            </>
          ) : (
            <>
              <Link to="/#Challenges">Challenges</Link>
              <Link to="/#Solution">Solution</Link>
              <Link to="/#howitworks">How It Works</Link>
            </>
          )}
        </nav>

        <div className="action-group">
          <div className="hamburger" onClick={() => setMenuOpen(true)}>
            ☰
          </div>

          {isLoggedIn ? (
            <button className="btn-logout" onClick={handleLogout}>
              Logout
            </button>
          ) : (
            <Link className="btn-primary" to="/signup">
              Get Started
            </Link>
          )}
        </div>
      </header>

      {/* Mobile Sidebar */}
      <div className={`mobile-sidebar ${menuOpen ? "active" : ""}`}>
        <div className="sidebar-header">
          <span className="close-btn" onClick={closeMenu}>✕</span>
        </div>

        <div className="sidebar-links">
          {isLoggedIn ? (
            <>
              <Link to="/dashboard" onClick={closeMenu}>Dashboard</Link>
              <Link to="/community-map" onClick={closeMenu}>Community Map</Link>
              <Link to="/contacts" onClick={closeMenu}>Contacts</Link>
            </>
          ) : (
            <>
              <Link to="/#Challenges" onClick={closeMenu}>Challenges</Link>
              <Link to="/#Solution" onClick={closeMenu}>Solution</Link>
              <Link to="/#howitworks" onClick={closeMenu}>How It Works</Link>
            </>
          )}
        </div>
      </div>

      {menuOpen && <div className="overlay" onClick={closeMenu}></div>}
    </>
  );
};

export default Header;
