import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

// --- STYLES ---
const DashboardStyles = () => (
  <style>{`
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; background-color: #e5e7eb; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 2rem; display: flex; flex-direction: column; overflow-y: auto; }
    .section-box { border: 1px solid #e5e7eb; border-radius: 8px; padding: 1.5rem; margin-bottom: 2rem; }
    h3 { margin-top: 0; }
    input { width: 100%; padding: 10px; margin-bottom: 10px; border: 1px solid #ddd; border-radius: 4px; box-sizing: border-box; }
    .btn-search { background: #4f46e5; color: white; width: 100%; padding: 10px; border: none; border-radius: 4px; cursor: pointer; }
    .btn-start { background: #10b981; color: white; width: 100%; padding: 12px; border: none; border-radius: 4px; cursor: pointer; font-weight: bold; margin-top: 10px; }
    .btn-share { background: #3b82f6; color: white; padding: 12px; border: none; border-radius: 4px; cursor: pointer; font-weight: bold; width: 48%; }
    .btn-end { background: #ef4444; color: white; padding: 12px; border: none; border-radius: 4px; cursor: pointer; font-weight: bold; width: 48%; }
    .action-row { display: flex; justify-content: space-between; margin-top: 20px; }
    .modal-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 2000; display: flex; justify-content: center; align-items: center; }
    .modal-content { background: white; padding: 20px; width: 350px; border-radius: 8px; }
    .contact-row { display: flex; justify-content: space-between; align-items: center; padding: 10px 0; border-bottom: 1px solid #eee; }
    .btn-send { background: #4f46e5; color: white; border: none; padding: 5px 10px; border-radius: 4px; cursor: pointer; }
  `}</style>
);

const Dashboard = () => {
  const [isActive, setIsActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [destination, setDestination] = useState('');
  const [tripDetails, setTripDetails] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [time, setTime] = useState(0);
  const socketRef = useRef();

  useEffect(() => {
    socketRef.current = io('http://localhost:5000');
    
    // Fetch Contacts
    const token = localStorage.getItem('token');
    if (token) {
        axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } })
             .then(res => setContacts(res.data))
             .catch(e => console.error(e));
    }
    return () => socketRef.current.disconnect();
  }, []);

  // Timer Effect
  useEffect(() => {
    let interval;
    if (isActive) {
        interval = setInterval(() => setTime(t => t + 1), 1000);
    } else {
        setTime(0);
    }
    return () => clearInterval(interval);
  }, [isActive]);

  const handleStartTrip = async () => {
    if (!destination) return alert("Please enter a destination first.");
    const token = localStorage.getItem('token');
    
    try {
        // Create trip
        const res = await axios.post('http://localhost:5000/api/trips/start', 
            { destination: { name: destination, lat: "0", lon: "0" } }, // Simplified for demo
            { headers: { 'x-auth-token': token } }
        );
        
        setTripDetails(res.data);
        setIsActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);
        alert("Trip Started!");
    } catch(e) {
        alert("Error starting trip. Ensure you are logged in.");
    }
  };

  const handleShare = (contact) => {
    // THIS IS THE FIX: Emit the notification
    socketRef.current.emit('notifyContacts', {
        tripId: tripDetails._id,
        senderName: "User", 
        contactName: contact.name
    });
    alert(`Alert sent to ${contact.name}!`);
  };

  const formatTime = (s) => new Date(s * 1000).toISOString().substr(11, 8);

  return (
    <div className="dashboard-layout">
      <DashboardStyles />
      <div className="map-column" style={{display:'flex', alignItems:'center', justifyContent:'center', color:'#666'}}>
        <h2>(Map View Here)</h2>
      </div>
      
      <div className="controls-column">
        {!isActive ? (
          <div className="section-box">
            <h3>Start a New Trip</h3>
            <input 
                placeholder="Enter destination..." 
                value={destination} 
                onChange={(e) => setDestination(e.target.value)} 
            />
            <button className="btn-start" onClick={handleStartTrip}>Start SafeWalk Trip</button>
            
            <div style={{marginTop: '20px'}}>
                <h4>Trusted Contacts <Link to="/contacts" style={{fontSize: '0.8rem'}}>Manage</Link></h4>
                {contacts.length === 0 && <p>No contacts found.</p>}
                {contacts.map(c => <div key={c._id} style={{padding:'5px'}}>ðŸ‘¤ {c.name}</div>)}
            </div>
          </div>
        ) : (
          <div className="section-box">
             <h3 style={{color: '#10b981', textAlign:'center'}}>Trip In Progress</h3>
             <div style={{textAlign:'center', fontSize:'3rem', fontWeight:'bold', margin:'20px 0'}}>
                {formatTime(time)}
             </div>
             <div className="action-row">
                <button className="btn-share" onClick={() => setShowModal(true)}>Share Link</button>
                <button className="btn-end" onClick={() => setIsActive(false)}>End Trip</button>
             </div>
          </div>
        )}
      </div>

      {/* Share Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Share with... <button onClick={() => setShowModal(false)} style={{float:'right'}}>x</button></h3>
            {contacts.map(c => (
              <div key={c._id} className="contact-row">
                <span>{c.name}</span>
                <button className="btn-send" onClick={() => handleShare(c)}>Send</button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
