import React, { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import "./style/Header.css";

const getAuthToken = () => localStorage.getItem("token");
const clearAuthToken = () => localStorage.removeItem("token");

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = getAuthToken();
    setIsLoggedIn(!!token);
  }, [location]);

  const handleLogout = () => {
    clearAuthToken();
    setIsLoggedIn(false);
    navigate("/");
  };

  const scrollTo = (id) => {
    navigate("/");
    setTimeout(() => {
      const el = document.getElementById(id);
      if (el) el.scrollIntoView({ behavior: "smooth" });
    }, 300);
  };

  return (
    <header className="header">
      {/* Logo */}
      <Link to="/" className="logo">SheSecure</Link>

      <div className="header-right">
        
        {/* Desktop Navigation */}
        {!isLoggedIn && (
          <nav className="nav-links desktop-nav">
            <button onClick={() => scrollTo("features")}>Features</button>
            <button onClick={() => scrollTo("howitworks")}>How it works</button>
            <button onClick={() => scrollTo("solutions")}>Solutions</button>
          </nav>
        )}

        {/* Dashboard links */}
        {isLoggedIn && (
          <nav className="nav-links">
            <Link to="/dashboard">Dashboard</Link>
            <Link to="/community-map">Community Map</Link>
            <Link to="/contacts">Contacts</Link>
          </nav>
        )}

        {/* Button */}
        <div className="action-group">
          {isLoggedIn ? (
            <button className="btn-logout" onClick={handleLogout}>Logout</button>
          ) : (
            <Link className="btn-primary" to="/signup">Get Started</Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
