// TrackTripPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

const API_URL = 'http://172.18.24.204:5000'; // your backend URL

const TrackTripPage = () => {
  const { tripId } = useParams();
  const [trip, setTrip] = useState(null);
  const [livePos, setLivePos] = useState(null);
  const [isEnded, setIsEnded] = useState(false);

  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const marker = useRef(null);

  const loadLeaflet = (cb) => {
    if (window.L) return cb();
    const s = document.createElement('script');
    s.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
    s.onload = cb;
    document.head.appendChild(s);

    const c = document.createElement('link');
    c.rel = 'stylesheet';
    c.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
    document.head.appendChild(c);
  };

  useEffect(() => {
    // 1. Initial trip data
    axios.get(`${API_URL}/api/trips/${tripId}`)
      .then(res => {
        setTrip(res.data);
        if (!res.data.isActive) setIsEnded(true);
        if (res.data.path && res.data.path.length > 0) {
          setLivePos(res.data.path[res.data.path.length - 1]);
        }
      })
      .catch(err => console.error('Initial trip load error', err));

    // 2. Socket connection
    const socket = io(API_URL);
    socket.emit('joinTripRoom', tripId);
    console.log('CLIENT: joined room', tripId);

    socket.on('tripUpdate', (coords) => {
      console.log('CLIENT: tripUpdate', coords);
      setLivePos(coords);
    });

    // When user ends trip, server emits tripEnded
    socket.on('tripEnded', async (payload) => {
      console.log('CLIENT: tripEnded event received', payload);
      setIsEnded(true);
      try {
        const res = await axios.get(`${API_URL}/api/trips/${tripId}`);
        setTrip(res.data); // now isActive=false, endedAt set
      } catch (err) {
        console.error('CLIENT: failed to refresh trip after end', err);
      }
    });

    // 3. Map init
    loadLeaflet(() => {
      if (mapRef.current && !mapObj.current) {
        const L = window.L;
        mapObj.current = L.map(mapRef.current).setView([26.76, 83.37], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png')
          .addTo(mapObj.current);

        delete L.Icon.Default.prototype._getIconUrl;
        L.Icon.Default.mergeOptions({
          iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
          iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
          shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
        });

        marker.current = L.marker([26.76, 83.37]).addTo(mapObj.current);
      }
    });

    return () => socket.disconnect();
  }, [tripId]);

  // move marker when new live position comes
  useEffect(() => {
    if (livePos && mapObj.current && window.L && marker.current) {
      const lat = parseFloat(livePos.lat);
      const lng = parseFloat(livePos.lng);
      if (!isNaN(lat) && !isNaN(lng)) {
        marker.current.setLatLng([lat, lng]);
        mapObj.current.setView([lat, lng]);
      }
    }
  }, [livePos]);

  return (
    <div style={{height: '100vh', display:'flex', flexDirection:'column'}}>
      <div
        style={{
          padding:'20px',
          background:'white',
          textAlign:'center',
          boxShadow:'0 2px 5px rgba(0,0,0,0.1)',
          zIndex:1000
        }}
      >
        <h2>Tracking {trip?.userId?.fullName || 'Trip'}</h2>
        {isEnded
          ? <h3 style={{color:'red'}}>TRIP ENDED</h3>
          : <h3 style={{color:'green'}}>LIVE TRACKING</h3>}
      </div>
      <div ref={mapRef} style={{flex:1}}></div>
    </div>
  );
};

export default TrackTripPage;
