import React, { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import "./style/Header.css";

const getAuthToken = () => localStorage.getItem('token');
const clearAuthToken = () => { localStorage.removeItem('token'); };

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const token = getAuthToken();
    setIsLoggedIn(!!token);
  }, [location]);

  const handleLogout = () => {
    clearAuthToken();
    setIsLoggedIn(false);
    navigate("/");
  };

  const closeMenu = () => setMenuOpen(false);

  return (
    <header className="header">
      <div className="header-container">
        
        {/* LEFT: Logo */}
        <Link to="/" className="logo" onClick={closeMenu}>SheSecure</Link>

        {/* CENTER: Nav Links (Desktop) / Dropdown (Mobile) */}
        <nav className={`nav-links ${menuOpen ? "active" : ""}`}>
          {isLoggedIn ? (
            <>
              <Link to="/dashboard" onClick={closeMenu}>Dashboard</Link>
              <Link to="/community-map" onClick={closeMenu}>Community Map</Link>
              <Link to="/contacts" onClick={closeMenu}>Contacts</Link>
            </>
          ) : (
            <>
              <a href="/#features" onClick={closeMenu}>Features</a>
              <a href="/#howitworks" onClick={closeMenu}>How It Works</a>
            </>
          )}
        </nav>

        {/* RIGHT: Button & Hamburger */}
        <div className="header-actions">
          {/* Button (Always visible) */}
          {isLoggedIn ? (
            <button className="btn-logout" onClick={handleLogout}>Logout</button>
          ) : (
            <Link className="btn-primary" to="/signup">Get Started</Link>
          )}

          {/* Hamburger (Mobile only) */}
          <div className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>
            <span className={menuOpen ? "bar open" : "bar"}></span>
            <span className={menuOpen ? "bar open" : "bar"}></span>
            <span className={menuOpen ? "bar open" : "bar"}></span>
          </div>
        </div>

      </div>
    </header>
  );
};

export default Header;
