import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

const API_URL = 'http://172.18.24.204:5000';

const TrackTripPageStyles = () => (
  <style>{`
    .track-container { width: 100%; height: 100vh; display: flex; flex-direction: column; background: #f3f4f6; }
    .track-header { padding: 1.5rem; background-color: #fff; box-shadow: 0 4px 15px rgba(0,0,0,0.1); z-index: 1000; text-align: center; flex-shrink: 0; }
    .track-header h1 { margin: 0; font-size: 1.4rem; color: #1f2937; }
    .status-badge { display: inline-block; padding: 6px 12px; border-radius: 20px; font-weight: bold; font-size: 0.9rem; margin-top: 10px; }
    .status-active { background: #dcfce7; color: #166534; border: 1px solid #bbf7d0; }
    .status-ended { background: #fee2e2; color: #991b1b; border: 1px solid #fecaca; }
    .loading-container, .error-container { display: flex; align-items: center; justify-content: center; height: 100vh; font-size: 1.2rem; color: #4b5563; }
    .error-text { color: #dc2626; }
    #map-container { flex-grow: 1; width: 100%; height: 100%; }
  `}</style>
);

const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const s = document.createElement('script'); s.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload = cb; document.head.appendChild(s);
  const c = document.createElement('link'); c.rel = 'stylesheet'; c.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(c);
};

const TrackTripPage = () => {
  const { tripId } = useParams();
  const [trip, setTrip] = useState(null);
  const [livePos, setLivePos] = useState(null);
  const [isEnded, setIsEnded] = useState(false);
  const [error, setError] = useState('');
  
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markerInstance = useRef(null);
  const socketRef = useRef(null);

  useEffect(() => {
    const fetchTrip = async () => {
      try {
        const res = await axios.get(`${API_URL}/api/trips/${tripId}`);
        setTrip(res.data);
        if (res.data.isActive === false) setIsEnded(true);
        if (res.data.path && res.data.path.length > 0) {
            setLivePos(res.data.path[res.data.path.length - 1]);
        }
      } catch (err) { setError('Could not load trip.'); }
    };
    fetchTrip();

    socketRef.current = io(API_URL);
    socketRef.current.emit('joinTripRoom', tripId);
    socketRef.current.on('tripUpdate', (coords) => setLivePos(coords));
    socketRef.current.on('tripEnded', () => { setIsEnded(true); alert("The trip has ended."); });

    loadLeaflet(() => {
        // Wait for container
        setTimeout(() => {
            if (window.L && mapRef.current && !mapInstance.current) {
                try {
                    const L = window.L;
                    // Fix Icons
                    delete L.Icon.Default.prototype._getIconUrl;
                    L.Icon.Default.mergeOptions({
                        iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
                        iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
                        shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
                    });

                    // Default view if no position yet
                    const initialLat = 26.7606;
                    const initialLng = 83.3732;

                    mapInstance.current = L.map(mapRef.current).setView([initialLat, initialLng], 13);
                    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstance.current);
                    markerInstance.current = L.marker([initialLat, initialLng]).addTo(mapInstance.current);
                } catch(e) { console.error("Map Init Error", e); }
            }
        }, 100);
    });

    return () => socketRef.current.disconnect();
  }, [tripId]);

  useEffect(() => {
    if (livePos && mapInstance.current && window.L) {
      const L = window.L;
      try {
          const lat = parseFloat(livePos.lat);
          const lng = parseFloat(livePos.lng);

          if (!isNaN(lat) && !isNaN(lng)) {
              markerInstance.current.setLatLng([lat, lng]);
              mapInstance.current.setView([lat, lng]);
          }
      } catch(e) { console.error("Update Error", e); }
    }
  }, [livePos]);

  if (error) return <div className="error-container"><TrackTripPageStyles /><p className="error-text">{error}</p></div>;
  if (!trip) return <div className="loading-container"><TrackTripPageStyles /><p>Connecting to SafeWalk...</p></div>;

  return (
    <div className="track-container">
      <TrackTripPageStyles />
      <div className="track-header">
        <h1>Tracking {trip.userId?.fullName || 'User'}</h1>
        <div className={`status-badge ${isEnded ? 'status-ended' : 'status-active'}`}>
            {isEnded ? 'TRIP ENDED' : 'LIVE TRACKING ACTIVE'}
        </div>
      </div>
      <div id="map-container" ref={mapRef}></div>
    </div>
  );
};

export default TrackTripPage;
