// --- 1. BEFORE TRIP PANEL ---
const BeforeTripPanel = ({ contacts, loading, error, searchTerm, setSearchTerm, onSearch, isSearching, searchResults, onSelectDestination, selectedDestination, onStartTrip }) => (
  <div className="panel-container">
    <div className="trip-planning-module">
      <h3>Start a New Trip</h3>
      <div className="destination-form">
        <label>Where are you going?</label>
        <div className="search-input-group">
          <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && onSearch()} />
          <button onClick={onSearch} disabled={isSearching}>{isSearching ? '...' : 'Search'}</button>
        </div>
        
        {searchResults.length > 0 && (
          <div className="results-section">
            <ul className="results-list">
              {searchResults.slice(0, 5).map((result) => (
                <li key={result.place_id} className={selectedDestination?.place_id === result.place_id ? 'selected' : ''} onClick={() => onSelectDestination(result)}>
                  {result.display_name}
                </li>
              ))}
            </ul>
          </div>
        )}
        <button className="start-trip-button" onClick={onStartTrip} disabled={!selectedDestination}>Start SafeWalk Trip</button>
      </div>
    </div>
    
    <div className="contacts-module">
      <div className="panel-header"><h3>Trusted Contacts</h3><Link to="/contacts" className="manage-link">Manage</Link></div>
      {loading && <p>Loading...</p>}
      {error && <p className="error-text">{error}</p>}
      {contacts.map(c => (
        <div key={c._id} className="contact-item">
          <div className="contact-avatar">{c.name.charAt(0)}</div>
          <div className="contact-details"><strong>{c.name}</strong><span>{c.phone}</span></div>
        </div>
      ))}
      {!loading && contacts.length === 0 && <p>No contacts added.</p>}
    </div>
  </div>
);

// --- 2. TRIP STATUS PANEL (With Share Modal) ---
const TripStatusPanel = ({ tripDetails, onEndTrip, contacts, socket }) => {
  const [time, setTime] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [sentStatus, setSentStatus] = useState({});

  useEffect(() => {
    const i = setInterval(() => setTime(Math.floor((new Date() - new Date(tripDetails.startedAt)) / 1000)), 1000);
    return () => clearInterval(i);
  }, [tripDetails]);

  const format = (s) => new Date(s * 1000).toISOString().substr(11, 8);

  const handleSend = (contact) => {
    setSentStatus(prev => ({ ...prev, [contact._id]: 'sending' }));
    if (socket) {
        socket.emit('notifyContacts', { 
            tripId: tripDetails._id, 
            senderName: "User", // Ideally get this from context
            contactName: contact.name 
        });
        setTimeout(() => {
            setSentStatus(prev => ({ ...prev, [contact._id]: 'sent' }));
            alert(`Alert sent to ${contact.name}!`);
        }, 500);
    }
  };

  return (
    <div className="panel-container">
      <h3 className="status-title">Trip in Progress</h3>
      <div className="timer-display"><p>ELAPSED TIME</p><span>{format(time)}</span></div>
      
      <div className="trip-actions">
        <button className="share-button" onClick={() => setShowModal(true)}>Share Link</button>
        <button className="end-trip-button" onClick={onEndTrip}>End Trip</button>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div style={{display:'flex', justifyContent:'space-between', marginBottom:'15px'}}>
                <h3>Share with Contacts</h3>
                <button onClick={() => setShowModal(false)} style={{border:'none', background:'none', fontSize:'1.5rem', cursor:'pointer'}}>Ã—</button>
            </div>
            {contacts.length === 0 ? <p>No contacts found.</p> : contacts.map(c => (
                <div key={c._id} className="share-row">
                    <div><strong>{c.name}</strong><br/><small>{c.phone}</small></div>
                    <button className="btn-send" disabled={sentStatus[c._id] === 'sent'} onClick={() => handleSend(c)}>
                        {sentStatus[c._id] === 'sent' ? 'Sent!' : 'Send'}
                    </button>
                </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

// --- 3. MAIN DASHBOARD ---
const Dashboard = () => {
  const [isTripActive, setIsTripActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [isSearching, setIsSearching] = useState(false);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPosition, setUserPosition] = useState({ lat: 26.7606, lng: 83.3732 });
  
  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // 1. Connect Socket
    socketRef.current = io('http://localhost:5000');

    // 2. Get Location
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserPosition({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => setError("Could not get user's location.")
    );

    // 3. Fetch Contacts
    const fetchContacts = async () => {
        const token = localStorage.getItem('token');
        if (!token) { navigate('/login'); return; }
        try {
            const res = await axios.get('http://localhost:5000/api/contacts', { headers: { 'x-auth-token': token } });
            setContacts(res.data);
        } catch (e) { setError('Failed to load contacts.'); } 
        finally { setLoading(false); }
    };
    fetchContacts();

    return () => {
      if (socketRef.current) socketRef.current.disconnect();
      if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, [navigate]);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    setIsSearching(true);
    try {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      setSearchResults(res.data);
    } catch (e) { setError('Search failed.'); } 
    finally { setIsSearching(false); }
  };

  const handleStartTrip = async () => {
    if (!selectedDestination) return alert("Select a destination!");
    const token = localStorage.getItem('token');
    try {
        const res = await axios.post('http://localhost:5000/api/trips/start', {
            destination: { name: selectedDestination.display_name, lat: selectedDestination.lat, lon: selectedDestination.lon }
        }, { headers: { 'x-auth-token': token } });
        
        setTripDetails(res.data);
        setIsTripActive(true);
        socketRef.current.emit('joinTripRoom', res.data._id);

        watchIdRef.current = navigator.geolocation.watchPosition(p => {
            const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
            setUserPosition(coords); // Update local map
            socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        }, console.error, { enableHighAccuracy: true });

    } catch (e) { alert("Error starting trip. Check connection."); }
  };

  const handleEndTrip = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setIsTripActive(false);
    setTripDetails(null);
    setSelectedDestination(null);
    setSearchTerm('');
    setSearchResults([]);
  };

  return (
    <>
      <DashboardStyles />
      <div className="dashboard-layout">
        <div className="map-column">
          {/* Passed props so marker updates in real-time */}
          <MapView userPosition={userPosition} destination={selectedDestination} isTripActive={isTripActive} />
        </div>
        <div className="controls-column">
          {isTripActive ? (
            <TripStatusPanel tripDetails={tripDetails} onEndTrip={handleEndTrip} contacts={contacts} socket={socketRef.current} />
          ) : (
            <BeforeTripPanel
              contacts={contacts} loading={loading} error={error} searchTerm={searchTerm}
              setSearchTerm={setSearchTerm} onSearch={handleSearch} isSearching={isSearching}
              searchResults={searchResults} onSelectDestination={setSelectedDestination}
              selectedDestination={selectedDestination} onStartTrip={handleStartTrip}
            />
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
