import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://172.18.24.204:5000'; // Your IP

const TrackTripPage = () => {
  const { tripId } = useParams();
  const [trip, setTrip] = useState(null);
  const [livePos, setLivePos] = useState(null);
  const [status, setStatus] = useState("loading"); // loading, active, ended
  
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const marker = useRef(null);

  // Load Map Helper
  const loadLeaflet = (cb) => {
    if (window.L) return cb();
    const c = document.createElement('link'); c.rel='stylesheet'; c.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(c);
    const s = document.createElement('script'); s.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; s.onload=cb; document.head.appendChild(s);
  };

  useEffect(() => {
    // 1. Initialize Map
    loadLeaflet(() => {
        const L = window.L;
        if(mapRef.current && !mapObj.current) {
             mapObj.current = L.map(mapRef.current).setView([26.76, 83.37], 13);
             L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
             
             const icon = new L.Icon({
                iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
                shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
                iconSize: [25, 41], iconAnchor: [12, 41]
             });
             marker.current = L.marker([26.76, 83.37], {icon}).addTo(mapObj.current);
        }
    });

    // 2. Start Polling Loop (Every 3 seconds)
    const interval = setInterval(() => {
        axios.get(`${API_URL}/api/trips/${tripId}`).then(res => {
            setTrip(res.data);
            
            // Check status
            if (!res.data.isActive) {
                setStatus("ended");
                // Optional: stop polling if ended
                // clearInterval(interval); 
            } else {
                setStatus("active");
            }

            // Update Position
            if (res.data.path && res.data.path.length > 0) {
                const latest = res.data.path[res.data.path.length - 1];
                setLivePos(latest);
                
                if (mapObj.current && window.L && marker.current) {
                    const lat = parseFloat(latest.lat);
                    const lng = parseFloat(latest.lng);
                    marker.current.setLatLng([lat, lng]);
                    mapObj.current.panTo([lat, lng]);
                }
            }
        }).catch(err => setStatus("error"));
    }, 3000);

    return () => clearInterval(interval);
  }, [tripId]);

  return (
    <div style={{height: '100vh', display:'flex', flexDirection:'column'}}>
       <div style={{padding:'20px', background:'white', textAlign:'center', boxShadow:'0 2px 5px rgba(0,0,0,0.1)', zIndex:1000}}>
           <h2>Tracking Trip</h2>
           {status === 'loading' && <p>Connecting...</p>}
           {status === 'active' && <h3 style={{color:'green', fontSize:'1.5rem'}}>LIVE TRACKING ACTIVE</h3>}
           {status === 'ended' && <h3 style={{color:'red', fontSize:'1.5rem'}}>TRIP ENDED</h3>}
       </div>
       <div ref={mapRef} style={{flex:1}}></div>
    </div>
  );
};

export default TrackTripPage;
