// Dashboard.js
import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

const API_URL = 'http://172.18.24.204:5000'; // backend URL

// ... all your styles, loaders, MapView, BeforeTripPanel, TripStatusPanel (unchanged) ...

// --- MAIN DASHBOARD COMPONENT ---
const Dashboard = () => {
  const [isLeafletReady, setIsLeafletReady] = useState(false);
  const [isTripActive, setIsTripActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [contactsError, setContactsError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [isSearching, setIsSearching] = useState(false);
  const [tripDetails, setTripDetails] = useState(null);
  const [userPosition, setUserPosition] = useState({ lat: 26.7606, lng: 83.3732 });
  const [searchError, setSearchError] = useState('');

  const socketRef = useRef(null);
  const watchIdRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadLeafletCSS();
    loadLeafletScript(() => setIsLeafletReady(true));
  }, []);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserPosition({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => setContactsError("Could not get user's location.")
    );

    const fetchContacts = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) throw new Error('Authorization failed.');
        const res = await axios.get(`${API_URL}/api/contacts`, {
          headers: { 'x-auth-token': token },
        });
        setContacts(res.data);
      } catch (err) {
        setContactsError('Could not load contacts.');
      } finally {
        setLoading(false);
      }
    };
    fetchContacts();

    socketRef.current = io(API_URL);
    return () => {
      if (socketRef.current) socketRef.current.disconnect();
      if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, []);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    setIsSearching(true);
    setSearchResults([]);
    setSearchError('');
    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`
      );
      if (response.data.length === 0) {
        setSearchError('No results found for that location.');
      }
      setSearchResults(response.data);
    } catch (err) {
      setSearchError('Search failed. Please check your network connection.');
    } finally {
      setIsSearching(false);
    }
  };

  const handleStartTrip = async () => {
    if (!selectedDestination) {
      alert("Please select a destination from the search results first.");
      return;
    }
    const token = localStorage.getItem('token');

    try {
      const res = await axios.post(
        `${API_URL}/api/trips/start`,
        {
          destination: {
            name: selectedDestination.display_name,
            lat: selectedDestination.lat,
            lon: selectedDestination.lon
          }
        },
        { headers: { 'x-auth-token': token } }
      );

      setTripDetails(res.data);
      setIsTripActive(true);

      socketRef.current.emit('joinTripRoom', res.data._id);

      watchIdRef.current = navigator.geolocation.watchPosition(
        (p) => {
          const coords = { lat: p.coords.latitude, lng: p.coords.longitude };
          setUserPosition(coords);
          socketRef.current.emit('updateLocation', { tripId: res.data._id, coordinates: coords });
        },
        console.error,
        { enableHighAccuracy: true }
      );
    } catch (e) {
      console.error(e);
      alert(`Error starting trip: ${e.message}`);
    }
  };

  const handleEndTrip = async () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);

    const token = localStorage.getItem('token');

    if (tripDetails && socketRef.current) {
      // notify contacts in real time
      socketRef.current.emit('endTrip', tripDetails._id);
      // update DB status
      try {
        await axios.post(
          `${API_URL}/api/trips/${tripDetails._id}/end`,
          {},
          { headers: { 'x-auth-token': token } }
        );
      } catch (e) {
        console.error("DB End Error", e);
      }
    }

    setIsTripActive(false);
    setTripDetails(null);
    setSelectedDestination(null);
    setSearchTerm('');
    setSearchResults([]);
    alert("Trip ended safely.");
  };

  return (
    <>
      <DashboardStyles />
      <div className="dashboard-layout">
        <div className="map-column">
          {isLeafletReady ? (
            <MapView userPosition={userPosition} destination={selectedDestination} />
          ) : (
            <p style={{textAlign: 'center', paddingTop: '2rem'}}>Loading Map Library...</p>
          )}
        </div>
        <div className="controls-column">
          {isTripActive ? (
            <TripStatusPanel 
              tripDetails={tripDetails} 
              onEndTrip={handleEndTrip} 
              contacts={contacts} 
            />
          ) : (
            <BeforeTripPanel
              contacts={contacts}
              loading={loading}
              error={contactsError}
              searchTerm={searchTerm}
              setSearchTerm={setSearchTerm}
              onSearch={handleSearch}
              isSearching={isSearching}
              searchResults={searchResults}
              onSelectDestination={setSelectedDestination}
              selectedDestination={selectedDestination}
              onStartTrip={handleStartTrip}
              searchError={searchError}
            />
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
