import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Polyline, useMap } from 'react-leaflet';
import L from 'leaflet';

// --- STYLES (Self-Contained) ---
// To resolve the file path errors, all necessary styles are now embedded
// directly within this single file, making it self-contained and runnable.
const DashboardStyles = () => (
  <style>{`
    /* --- Main Layout --- */
    .dashboard-layout { display: flex; height: calc(100vh - 70px); background-color: #f3f4f6; }
    .map-column { flex: 3; }
    .controls-column { flex: 2; background-color: #ffffff; padding: 1.5rem 2rem; box-shadow: -5px 0 15px rgba(0, 0, 0, 0.05); display: flex; flex-direction: column; overflow-y: auto; }

    /* --- Before Trip Panel --- */
    .panel-container { display: flex; flex-direction: column; height: 100%; }
    .search-section h3 { margin-top: 0; }
    .search-input-group { display: flex; gap: 0.5rem; margin-bottom: 1rem; }
    .search-input-group input { flex-grow: 1; padding: 0.75rem; border: 1px solid #d1d5db; border-radius: 8px; font-size: 1rem; }
    .search-input-group button { background-color: #4f46e5; color: white; border: none; border-radius: 8px; padding: 0 1rem; cursor: pointer; }
    .search-input-group button:disabled { background-color: #a5b4fc; }
    .results-section { margin-bottom: 1rem; max-height: 200px; overflow-y: auto; }
    .results-list { list-style: none; padding: 0; margin: 0; border: 1px solid #e5e7eb; border-radius: 8px; }
    .results-list li { padding: 0.75rem 1rem; border-bottom: 1px solid #e5e7eb; cursor: pointer; font-size: 0.9rem; }
    .results-list li:last-child { border-bottom: none; }
    .results-list li:hover { background-color: #f3f4f6; }
    .results-list li.selected { background-color: #e0e7ff; font-weight: bold; }
    .panel-header { border-top: 1px solid #e5e7eb; padding-top: 1rem; }
    .contact-list { flex-grow: 1; }
    .start-trip-button { width: 100%; padding: 1rem; margin-top: 1rem; font-size: 1.1rem; font-weight: bold; color: white; background-color: #6366f1; border: none; border-radius: 8px; cursor: pointer; }
    .start-trip-button:disabled { background-color: #a5b4fc; cursor: not-allowed; }
    .error-text { color: #ef4444; }

    /* --- Trip Status Panel --- */
    .status-title { text-align: center; font-size: 1.5rem; color: #111827; margin-bottom: 2rem; }
    .timer-display { text-align: center; margin: auto; }
    .timer-display p { margin: 0; color: #6b7280; font-size: 1rem; }
    .timer-display span { font-size: 4rem; font-weight: bold; }
    .trip-actions { display: flex; gap: 1rem; margin-top: 2rem; }
    .share-button, .end-trip-button { flex: 1; padding: 1rem; font-size: 1rem; font-weight: bold; border-radius: 8px; cursor: pointer; border: none; }
    .share-button { background-color: #f3f4f6; }
    .end-trip-button { background-color: #ef4444; color: white; }
  `}</style>
);

// --- MARKER ICON FIX ---
// This is a known issue with React-Leaflet. This code fixes the missing marker icons.
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});


// --- SUB-COMPONENTS (Now defined inside the main file) ---

const FitBounds = ({ user, dest }) => {
  const map = useMap();
  useEffect(() => {
    if (user && dest) {
      const bounds = L.latLngBounds([user.lat, user.lng], [dest.lat, dest.lon]);
      map.fitBounds(bounds.pad(0.2));
    } else if (user) {
      map.setView([user.lat, user.lng], 15);
    }
  }, [user, dest, map]);
  return null;
};

const MapView = ({ isTripActive, destination }) => {
  const [userPosition, setUserPosition] = useState({ lat: 26.7606, lng: 83.3732 });

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserPosition({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      () => console.error("Error getting user location.")
    );
  }, [isTripActive]);

  return (
    <MapContainer center={[userPosition.lat, userPosition.lng]} zoom={13} style={{ height: '100%', width: '100%' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors' />
      <Marker position={[userPosition.lat, userPosition.lng]} />
      {destination && <Marker position={[destination.lat, destination.lon]} />}
      {destination && <Polyline pathOptions={{ color: '#4f46e5' }} positions={[[userPosition.lat, userPosition.lng], [destination.lat, destination.lon]]} />}
      <FitBounds user={userPosition} dest={destination} />
    </MapContainer>
  );
};

const BeforeTripPanel = ({ contacts, loading, error, searchTerm, setSearchTerm, onSearch, isSearching, searchResults, onSelectDestination, selectedDestination, onStartTrip }) => (
  <div className="panel-container">
    <div className="search-section">
      <h3>Plan Your Trip</h3>
      <div className="search-input-group">
        <input type="text" placeholder="Enter destination..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && onSearch()} />
        <button onClick={onSearch} disabled={isSearching}>{isSearching ? '...' : 'Search'}</button>
      </div>
    </div>
    {searchResults.length > 0 && (
      <div className="results-section">
        <h4>Select a Destination:</h4>
        <ul className="results-list">
          {searchResults.slice(0, 5).map((result) => (
            <li key={result.place_id} className={selectedDestination?.place_id === result.place_id ? 'selected' : ''} onClick={() => onSelectDestination(result)}>
              {result.display_name}
            </li>
          ))}
        </ul>
      </div>
    )}
    <div className="panel-header"><h3>Trusted Contacts</h3></div>
    <div className="contact-list">{loading ? <p>Loading contacts...</p> : error ? <p className="error-text">{error}</p> : null}</div>
    <button className="start-trip-button" onClick={onStartTrip} disabled={!selectedDestination}>Start SafeWalk Trip</button>
  </div>
);

const TripStatusPanel = ({ tripDetails, onEndTrip }) => {
    // Timer logic and other state specific to this panel
    return (
        <div className="panel-container">
            <h3 className="status-title">Trip in Progress</h3>
            <div className="timer-display"><p>ELAPSED TIME</p><span>00:00:00</span></div>
            <div className="trip-actions"><button className="share-button">Share Link</button><button className="end-trip-button" onClick={onEndTrip}>End Trip</button></div>
        </div>
    );
};


// --- MAIN DASHBOARD COMPONENT ---
const Dashboard = () => {
  const [isTripActive, setIsTripActive] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [isSearching, setIsSearching] = useState(false);
  const [tripDetails, setTripDetails] = useState(null);

  useEffect(() => {
    const fetchContacts = async () => {
      // Placeholder for your actual contact fetching logic
    };
    fetchContacts();
  }, []);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    setIsSearching(true);
    setSearchResults([]);
    try {
      const response = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${searchTerm}`);
      setSearchResults(response.data);
    } catch (err) {
      setError('Could not fetch location. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  const handleStartTrip = () => {
    if (!selectedDestination) {
      alert("Please select a destination before starting a trip.");
      return;
    }
    const newTrip = { id: `trip_${new Date().getTime()}`, startTime: new Date(), destination: selectedDestination };
    setTripDetails(newTrip);
    setIsTripActive(true);
    alert("Your SafeWalk trip has started.");
  };

  const handleEndTrip = () => {
    setIsTripActive(false);
    setTripDetails(null);
    setSelectedDestination(null);
    setSearchTerm('');
    setSearchResults([]);
    alert("Your trip has safely ended.");
  };

  return (
    <>
      <DashboardStyles />
      <div className="dashboard-layout">
        <div className="map-column">
          <MapView isTripActive={isTripActive} destination={selectedDestination} />
        </div>
        <div className="controls-column">
          {isTripActive ? (
            <TripStatusPanel tripDetails={tripDetails} onEndTrip={handleEndTrip} />
          ) : (
            <BeforeTripPanel
              contacts={contacts}
              loading={loading}
              error={error}
              searchTerm={searchTerm}
              setSearchTerm={setSearchTerm}
              onSearch={handleSearch}
              isSearching={isSearching}
              searchResults={searchResults}
              onSelectDestination={setSelectedDestination}
              selectedDestination={selectedDestination}
              onStartTrip={handleStartTrip}
            />
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
