import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// --- Import Components ---
import Header from './HomePage/Header';
import Footer from './HomePage/Footer';
import LandingPage from './Pages/LandingPage';
import Signup from './Pages/Signup';
import Login from './Pages/Login';
import Logout from './Pages/Logout';
import Dashboard from './Pages/Dashboard';
import ManageContacts from './Pages/ManageContacts';
import TrackTripPage from './Pages/TrackTripPage';
import PrivateRoute from './components/PrivateRoute';

// --- ACTION: Import the Notification Listener we just made ---
import NotificationListener from './components/NotificationListener'; 

// --- STYLES (Self-Contained) ---
const AppStyles = () => (
  <style>{`
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; margin: 0; background-color: #f9fafb; }
    .App { text-align: center; }
    main { padding-top: 70px; min-height: calc(100vh - 130px); }
  `}</style>
);

function App() {
  return (
    <Router>
      <AppStyles />
      <div className="App">
        <Header />
        
        {/* --- ACTION: Place the listener here so it's active on all pages --- */}
        <NotificationListener /> 
        
        <main>
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<LandingPage />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/logout" element={<Logout />} />
            <Route path="/track/:tripId" element={<TrackTripPage />} />
            
            {/* Protected Routes */}
            <Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />
            <Route path="/contacts" element={<PrivateRoute><ManageContacts /></PrivateRoute>} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
