import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';

const loadLeaflet = (cb) => {
  if (window.L) return cb();
  const link = document.createElement('link'); link.rel='stylesheet'; link.href='https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; document.head.appendChild(link);
  const script = document.createElement('script'); script.src='https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; script.onload=cb; document.head.appendChild(script);
};

const TrackTripPage = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState(null);
  const [isEnded, setIsEnded] = useState(false);
  const [livePos, setLivePos] = useState(null);
  const mapRef = useRef(null);
  const mapObj = useRef(null);
  const marker = useRef(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/trips/${tripId}`).then(res => {
        setTrip(res.data);
        if(!res.data.isActive) setIsEnded(true);
    });

    const socket = io('http://localhost:5000');
    socket.emit('joinTripRoom', tripId);
    
    socket.on('tripUpdate', (coords) => setLivePos(coords));
    
    // FIX #4: Listen for End Trip event
    socket.on('tripEnded', () => {
        setIsEnded(true);
        alert("The user has ended this trip.");
    });

    loadLeaflet(() => {
        if(mapRef.current && !mapObj.current) {
            const L = window.L;
            mapObj.current = L.map(mapRef.current).setView([26.76, 83.37], 13);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapObj.current);
            marker.current = L.marker([26.76, 83.37]).addTo(mapObj.current);
        }
    });

    return () => socket.disconnect();
  }, [tripId]);

  useEffect(() => {
      if(livePos && mapObj.current && window.L) {
          marker.current.setLatLng([livePos.lat, livePos.lng]);
          mapObj.current.setView([livePos.lat, livePos.lng]);
      }
  }, [livePos]);

  return (
    <div style={{height: '100vh', display:'flex', flexDirection:'column'}}>
       <div style={{padding:'20px', background:'white', boxShadow:'0 2px 5px rgba(0,0,0,0.1)', zIndex:1000, display:'flex', justifyContent:'space-between', alignItems:'center'}}>
           <div>
               <h2>Tracking Trip</h2>
               {/* FIX #4: UI Update */}
               {isEnded ? <span style={{color:'red', fontWeight:'bold'}}>TRIP ENDED</span> : <span style={{color:'green'}}>LIVE TRACKING</span>}
           </div>
           {/* FIX #3: Stop Tracking Button */}
           <button onClick={() => navigate('/')} style={{padding:'10px', background:'#333', color:'white', border:'none', borderRadius:'5px'}}>
               Stop Tracking
           </button>
       </div>
       <div ref={mapRef} style={{flex:1}}></div>
    </div>
  );
};
export default TrackTripPage;
